<?phpclass AjaxadvAction extends BaseAction{	        /**        * 广告调用        */    public function index(){            $get_type = $_POST['get_type'];            $value_id = $_POST['value_id'];            $limit = intval($_POST['limit']);                $wap_dis = D('Adver')->get_adver_by_key($get_type.'_'.$value_id,$limit);                if(!$wap_dis){                    $wap_dis = D('Adver')->get_adver_by_key($get_type,$limit);                }             echo json_encode($wap_dis);             exit;    }}?>