<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Area/index')}" >根列表</a>|															<a href="javascript:history.go(-1)" >上一级</a>|															<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Area/circle_category_add')}','添加商圈分类',450,100,true,false,false,addbtn,'store_add',true);">添加商圈分类</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>														<col width="240" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>名称</th>								<th>操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($circle_category_list)">								<volist name="circle_category_list" id="vo">									<tr>																			<td>{pigcms{$vo.id}</td>										<td><font color="red">{pigcms{$vo.name}</font></td>																				<td>											<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Area/circle_category_add',array('category_id'=>$vo['id']))}','编辑商圈分类',450,100,true,false,false,editbtn,'store_add',true);">编辑</a> |											<a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('Area/circle_category_del')}">删除</a>																					</td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="8">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>