<include file="Public:header"/><div class="mainbox">    <div id="nav" class="mainnav_title">        <ul>            <a href="{pigcms{:U('Article/index')}" class="on">分类列表</a>|            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Article/create')}','添加分类',480,260,true,false,false,addbtn,'add',true);">添加分类</a>|            <a href="{pigcms{:U('Article/article_list')}">文章列表</a>|            <a href="{pigcms{:U('Article/comment_list')}">评论列表</a>        </ul>    </div>    <table class="search_table" width="100%">        <tr>            <td>                <form action="{pigcms{:U('Article/index')}" method="get">                    <input type="hidden" name="c" value="Article"/>                    <input type="hidden" name="a" value="index"/>                    名称: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>                    <input type="submit" value="查询" class="button"/>                </form>            </td>        </tr>    </table>    <form name="myform" id="myform" action="" method="post">        <div class="table-list">            <table width="100%" cellspacing="0">                <colgroup>                    <col/>                    <col/>                    <col/>                    <col/>                    <col width="180" align="center"/>                </colgroup>                <thead>                <tr>                    <th>排序</th>                    <th>编号</th>                    <th>名称</th>                    <th>文章总数</th>                    <!--<th>短标记(url)</th>                   if condition="empty($parentid)">                       <th>查看子分类</th>                   </if>-->                    <th>状态</th>                    <th class="textcenter">操作</th>                </tr>                </thead>                <tbody>                <if condition="is_array($categoryList)">                    <volist name="categoryList" id="vo">                        <tr>                            <td>{pigcms{$vo.order}</td>                            <td>{pigcms{$vo.id}</td>                            <td>{pigcms{$vo.name}</td>                            <td>{pigcms{$vo.article_total}</td>                            <!-- <td>{pigcms{$vo.cat_url}</td>                             <if condition="empty($parentid)">                                 <td><a href="{pigcms{:U('Meal/index',array('parentid'=>$vo['cat_id']))}">查看子分类</a></td>                             </if>-->                            <td><if condition="$vo['status'] eq 1"><font color="green">启用</font><elseif condition="$vo['status'] eq 2"/><font color="red">禁用</font></if></td>                            <td class="textcenter">                                <!--<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Article/edit',array('id'=>$vo['id'],'frame_show'=>true))}','查看分类信息',480,260,true,false,false,false,'detail',true);">查看</a> | -->                                <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Article/edit',array('id'=>$vo['id']))}','编辑分类信息',480,260,true,false,false,editbtn,'edit',true);">编辑</a> |                                <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('cat_del')}">删除</a>                            </td>                        </tr>                    </volist>                    <tr><td class="textcenter pagebar" colspan="7">{pigcms{$page}</td></tr>                    <else/>                    <tr><td class="textcenter red" colspan="6">列表为空！</td></tr>                </if>                </tbody>        </div>    </form></div><include file="Public:footer"/>