<include file="Public:header"/><style>	.station{width: 150px; height: 40px; float: left;}</style><form id="myform" method="post" action="{pigcms{:U('Bbs/create_data')}" >	<table cellpadding="0" cellspacing="0" class="frame_form" width="100%">		<tr>			<td width="80">论坛名称</td>			<td>				<input type="text" class="input fl" name="name" value="" placeholder="请填写论坛名称" validate="required:true"></td>		</tr> 		<tr>			<td width="80">是否开启</td>			<td class="radio_box">				<span class="cb-enable">					<label class="cb-enable ">						<span>启用</span>						<input type="radio" name="is_display" value="1" ></label>				</span>								<span class="cb-disable">					<label class="cb-disable selected">						<span>禁用</span>						<input type="radio" name="is_display" value="0" checked="checked"></label>				</span>			</td>		</tr>		<tr>			<td width="80">论坛所属</td>			<td class="radio_box">				<span class="cb-enable">					<label class="cb-enable selected">						<span>小区</span>						<input type="radio" name="owners" value="1" checked="checked"></label>				</span>				<span class="cb-disable">					<label class="cb-disable">						<span>楼盘</span>						<input type="radio" name="owners" value="2" ></label>				</span>			</td>		</tr>		<tr class="village">			<td width="80">选择小区：</td>			<td>				<select id="stationSelect" name="village_id" class="">					<option value=''>== 请选小区 ==</option>                    <if condition="is_array($villages)">                        <volist name="villages" id="vo">					<option value='{pigcms{$vo.village_id}'>{pigcms{$vo.name}</option>                        </volist>                    </if>				</select>			</td>		</tr>        <tr class="building" style="display: none;">            <td width="80">选择楼盘：</td>            <td>                <select id="stationSelect" name="id" class="" >                    <option value=''>== 请选楼盘 ==</option>                    <if condition="is_array($building)">                        <volist name="building" id="vo">                            <option value='{pigcms{$vo.id}'>{pigcms{$vo.title}</option>                        </volist>                    </if>                </select>            </td>        </tr>	</table>	<script>        $(function(){            $('input[name="owners"]').click(function(){                var owners = $(this).val();                if(owners == 1){                    $('.village').css('display','').next().css('display','none')                } else if (owners == 2) {                    $('.building').css('display','').prev().css('display','none')                }            });        })	</script>	<div class="btn hidden">		<input type="submit" name="dosubmit" id="dosubmit" value="提交" class="button" />		<input type="reset" value="取消" class="button" />	</div></form><include file="Public:footer"/>