<include file="Public:header"/><style>    .station{width: 150px; height: 40px; float: left;}</style><form id="myform" method="post" action="{pigcms{:U('Bbs/edit_add')}" >    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <td width="80">论坛名称</td>            <td>                <input type="text" class="input fl" name="name" value="{pigcms{$bbsInfo.name}" placeholder="请填写论坛名称" validate="required:true"></td>                <input type="hidden" class="input fl" name="id" value="{pigcms{$bbsInfo.id}">        </tr>        <tr>            <td width="80">是否开启</td>            <td class="radio_box">				<span class="cb-enable">					<label class="cb-enable <?php if($bbsInfo['is_display'] == 1) {?>selected <?php }?>">                        <span>启用</span>                        <input type="radio" name="is_display" value="1" <?php if($bbsInfo['is_display'] == 1) {?>checked <?php }?>></label>				</span>								<span class="cb-disable">					<label class="cb-disable <?php if(empty($bbsInfo['is_display'])) {?>selected <?php }?>">                        <span>禁用</span>                        <input type="radio" name="is_display" value="0" <?php if(empty($bbsInfo['is_display'])) {?>checked <?php }?>></label>				</span>            </td>        </tr>        <!--<tr>            <td width="80">状态</td>            <td class="radio_box">                <span class="cb-enable">                    <label class="cb-enable selected">                        <span>正常</span>                        <input type="radio" name="status" value="1" checked="checked"></label>                </span>                <span class="cb-disable">                    <label class="cb-disable">                        <span>禁止</span>                        <input type="radio" name="status" value="2"></label>                </span>            </td>        </tr>-->        <if condition="$owners eq 1">        <tr>            <td width="80">选择小区：</td>            <td>                <select id="stationSelect" name="village_id" class="" >                    <option value='{pigcms{$bbsInfo.village_id}'>{pigcms{$bbsInfo.name}</option>                    <if condition="is_array($villages)">                        <volist name="villages" id="vo">                            <if condition="$vo['village_id'] neq $bbsInfo['village_id']">                            <option value='{pigcms{$vo.village_id}'>{pigcms{$vo.name}</option>                            </if>                        </volist>                    </if>                </select>            </td>        </tr>        <elseif condition="$owners eq 2"/>            <tr>                <td width="80">选择楼盘：</td>                <td>                    <select id="stationSelect" name="building" class="">                        <option value='{pigcms{$bbsInfo.building}'>{pigcms{$bbsInfo.name}</option>                        <if condition="is_array($building)">                            <volist name="building" id="vo">                                <if condition="$vo['id'] neq $bbsInfo['building_id']">                                    <option value='{pigcms{$vo.building_id}'>{pigcms{$vo.title}</option>                                </if>                            </volist>                        </if>                    </select>                </td>            </tr>        </if>    </table>    <script>    </script>    <div class="btn hidden">        <input type="submit" name="dosubmit" id="dosubmit" value="提交" class="button" />        <input type="reset" value="取消" class="button" />    </div></form><include file="Public:footer"/>