<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Bbs/index')}" class="on">论坛列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Bbs/create')}','新建论坛',600,300,true,false,false,addbtn,'add',true);">新建论坛</a>|				</ul>			</div>            <table class="search_table" width="100%">                <tr>                    <td>                        <form action="{pigcms{:U('Bbs/index')}" method="get">                            <input type="hidden" name="c" value="Bbs"/>                            <input type="hidden" name="a" value="index"/>                            筛选: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>                            <select name="type">                                <option value="name" <if condition="$_GET['type'] eq 'name'">selected="selected"</if>>论坛名称</option>                                <option value="budding" <if condition="$_GET['type'] eq 'budding'">selected="selected"</if>>小区/楼盘</option>                            </select>                            <input type="submit" value="查询" class="button"/>                        </form>                    </td>                </tr>            </table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>名称</th>								<th>小区/楼盘</th>								<!--<th>状态</th>-->								<th>总帖数</th>								<th>创建时间</th>								<th>操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($fcbbsList)">								<volist name="fcbbsList" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.name}</td>                                        <if condition="$vo['owners'] eq 1">										<td>{pigcms{$vo.viliage_name}</td>                                        <elseif condition="$vo['owners'] eq 2"/>                                        <td>{pigcms{$vo.building_name}</td>                                        </if>										<!--<td>											<if condition="$vo['is_display'] eq 0"><font color="red">关闭中</font><elseif condition="$vo['is_display'] eq 1"/><font color="green">正常</font></if>										</td>-->                                        <td>{pigcms{$vo.total_posts}</td>                                        <td>{pigcms{$vo.create_time|date='Y-m-d H:i:s',###}</td>										<td style="text-align: right;">                                            <span class="cb-enable">                                                <label class="cb-enable <if condition="$vo['is_display'] eq 1"> selected </if>">                                                    <span class="js-enable" data-id="{pigcms{$vo.id}">启用</span>                                                    <input type="radio" name="is_display" value="1"  <if condition="$vo['is_display'] eq 1"> checked </if> ></label>                                            </span>                                            <span class="cb-disable">                                                <label class="cb-disable <if condition="$vo['is_display'] eq 0"> selected </if>">                                                    <span class="js-disable" data-id="{pigcms{$vo.id}">禁用</span>                                                    <input type="radio" name="is_display" value="0" <if condition="$vo['is_display'] eq 0"> checked </if>></label>                                            </span>											<!--<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('edit',array('id'=>$vo['id']))}','编辑论坛',600,300,true,false,false,editbtn,'edit',true);">编辑</a>|											<a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('bbs_del')}">删除</a>-->									  	</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="7">{pigcms{$page}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="4">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><script>    $(function(){       $('.js-enable').click(function(){ //启用           var id = $(this).data('id');           $.post("{pigcms{:U('Bbs/bbs_disable')}",{'id':id,'disable':1},function(){           })       });       $('.js-disable').click(function(){ //禁用           var id = $(this).data('id');           $.post("{pigcms{:U('Bbs/bbs_disable')}",{'id':id,'disable':0},function(){           })       });    });</script><include file="Public:footer"/>