:U('Bill/billed_list',array('mer_id'=>$mer_id, 'type' => 'meal'))}" <if condition="$type eq 'meal'">class="on"</if>>{pigcms{$config.meal_alias_name}账单<div id="meal"></div></a></if>					<if condition="$bill_time.group_time gt 0"><a href="{pigcms{:U('Bill/billed_list',array('mer_id'=>$mer_id, 'type' => 'group'))}" <if condition="$type eq 'group'">class="on"</if>>{pigcms{$config.group_alias_name}账单<div id="group"></div></a></if>					<php>if($bill_time['appoint_time']>0){</php><if condition="$config['appoint_page_row'] gt 0">						<a href="{pigcms{:U('Bill/billed_list',array('mer_id'=>$mer_id, 'type' => 'appoint'))}" <if condition="$type eq 'appoint'">class="on"</if>>{pigcms{$config.appoint_alias_name}账单<div id="appoint"></div></a>						</if><php>}</php>					<php>if($bill_time['waimai_time']>0){</php><if condition="$config['waimai_alias_name']">						<a href="{pigcms{:U('Bill/billed_list',array('mer_id'=>$mer_id, 'type' => 'waimai'))}" <if condition="$type eq 'waimai'">class="on"</if>>{pigcms{$config.waimai_alias_name}账单<div id="waimai"></div></a>					</if><php>}</php>					<if condition="$bill_time.shop_time gt 0"><a href="{pigcms{:U('Bill/billed_list',array('mer_id'=>$mer_id, 'type' => 'shop'))}" <if condition="$type eq 'shop'">class="on"</if>>{pigcms{$config.shop_alias_name}账单<div id="shop"></div></a></if>					<php>if($bill_time['store_time']>0){</php><if condition="$config['is_cashier'] OR $config['pay_in_store']">					<a href="{pigcms{:U('Bill/billed_list',array('mer_id'=>$mer_id, 'type' => 'store'))}" <if condition="$type eq 'store'">class="on"</if>>到店付账单<div id="store"></div></a>					</if><php>}</php>					<php>if($bill_time['weidian_time']>0){</php><if condition="$config['is_open_weidian']">						<a href="{pigcms{:U('Bill/billed_list',array('mer_id'=>$mer_id, 'type' => 'weidian'))}" <if condition="$type eq 'weidian'">class="on"</if>>微店账单<div id="weidian"></div></a>					</if><php>}</php>					<php>if($bill_time['wxapp_time']>0){</php>						<if condition="$config['wxapp_url']">							<a href="{pigcms{:U('Bill/billed_list',array('mer_id'=>$mer_id, 'type' => 'wxapp'))}" <if condition="$type eq 'wxapp'">class="on"</if>>营销账单<div id="wxapp"></div></a>						</if>					<php>}</php>				</ul>			</div>									<input type="hidden" id="com_pay_money"name="money" value="">				<input type="hidden" name="mer_id" value="{pigcms{$mer_id}">				<input type="hidden" name="pay_type" value="{pigcms{$type}">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col width="200"/>							<col />							<col />							<col/>												</colgroup>						<thead>							<tr>								<th>对账时间</th>								<th>对账总金额</th>								<th>对账总订单数</th>								<th>操作</th>							</tr>						</thead>						<tbody>								<if condition="$bill_list">									<volist name="bill_list" id="vo">										<tr>											<td>{pigcms{$vo.bill_time|date="Y/m/d H:i",###}</td>											<td>{pigcms{$vo['money']/100}元</td>											<td>{pigcms{:count($vo['id_list'])}个</td>											<td><a href="{pigcms{:U('Bill/billed_info',array('id'=>$vo['id']))}"><button>查看</button></a></td>										</tr>									</volist>																	<tr><td class="textcenter pagebar" colspan="4">{pigcms{$pagebar}</td></tr>									<else/>									<tr class="odd"><td class="textcenter red" colspan="17" >该的店铺暂时还没有订单。</td></tr>								</if>						</tbody>					</table>				</div>				</div><script type="text/javascript" src="{pigcms{$static_public}js/artdialog/jquery.artDialog.js"></script><script type="text/javascript" src="{pigcms{$static_public}js/artdialog/iframeTools.js"></script><include file="Public:footer"/>