<include file="Public:header"/><style>.mainnav_title ul a {    padding: 15px 20px;}ul, ol {    margin-bottom: 15px;}.mainnav_title div{	color:#7EBAEF;	float:right;}.mainnav_title a.on div{	color:#C1BEBE;}.info{	line-height:30px;}.table-list thead th {    text-align: center;}.table-list tbody td {    text-align: center;	white-space: nowrap;　　overflow: hidden;　　text-overflow: ellipsis;}button{	    margin: 5px;	padding: 6px;	background-color: rgba(255, 255, 255, 0);	box-sizing: border-box;	border-width: 1px;	border-style: solid;	border-color: rgba(121, 121, 121, 1);	border-radius: 2px;	-moz-box-shadow: none;	-webkit-box-shadow: none;	box-shadow: none;	font-size: 16px;	color: #666666;	cursor: pointer ;}</style>		<div class="mainbox">		<div class="mainnav_title" style="margin-top: 12px;">			<a href="{pigcms{:U('Bill/express_statistics')}">上一页</a>		</div>			<div style="margin:15px 0;">				<b>商家ID：</b>{pigcms{$now_merchant.mer_id}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>商家名称：</b>{pigcms{$now_merchant.name}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>联系电话：</b>{pigcms{$now_merchant.phone}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>				配送总订单数：</b>{pigcms{$deliver_num} &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 配送总金额：</b>{pigcms{$deliver_money|floatval}元			</div>						<div class="mainnav_title">					<form id="date" method="get" action="{pigcms{:U('Bill/express_statistics_mer')}" >							<input type="hidden" name="c" value="Bill"/>							<input type="hidden" name="a" value="express_statistics_mer"/>							<font color="#000">订单：<input type="text" name="order_id" value="{pigcms{$order_id}" placeholder="输入订单号" style="    line-height: 18px;height: 18px;">													<input type="hidden" name="mer_id" value="{pigcms{$mer_id}">													<font color="#000">日期筛选：</font>							<input type="text" class="input-text" name="begin_time" style="width:120px;" id="d4311"  value="{pigcms{$begin_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>			   							<input type="text" class="input-text" name="end_time" style="width:120px;" id="d4311" value="{pigcms{$end_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>																												<input type="submit" value="筛选" class="button">											</form>					&nbsp;&nbsp;&nbsp;&nbsp;<a href="javascript:void(0)" onclick="exports();" >导出EXCEL</a>							</div>							<input type="hidden" id="com_pay_money"name="money" value="">				<input type="hidden" name="mer_id" value="{pigcms{$mer_id}">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col width="200"/>							<col />							<col/>						</colgroup>						<thead>							<tr>								<th>店铺名称</th>								<th>订单号</th>								<th>数量</th>								<th>订单总价</th>								<th>配送费</th>																<th>支付时间</th>										<th>详情</th>							</tr>						</thead>						<tbody>								<if condition="$deliver_list">								<volist name="deliver_list" id="vo">									<tr>										<td>{pigcms{$vo.store_name}</td>										<td>{pigcms{$vo.real_orderid}</td>																			<td>{pigcms{$vo.num}</td>										<td>{pigcms{$vo.money|floatval}元</td>										<td>{pigcms{$vo.freight_charge|floatval}元</td>										<td><if condition="$vo['pay_time'] gt 0">{pigcms{$vo.pay_time|date="Y-m-d H:i:s",###}</if></td>																				<td>																															<a href="javascript:void(0);" onclick="window.top.artiframe('/admin.php?g=System&c=Shop&a=order_detail&order_id={pigcms{$vo.order_id}','查看订单详情',600,460,true,false,false,false,'order_edit',true);"><button>查看详情</button></a>																				</td>									</tr>								</volist>																	<tr><td class="textcenter pagebar" colspan="12">{pigcms{$pagebar}</td></tr>									<else/>									<tr class="odd"><td class="textcenter red" colspan="12" >没有搜索到结果</td></tr>								</if>						</tbody>					</table>				</div>				</div><script type="text/javascript" src="{pigcms{$static_public}js/artdialog/jquery.artDialog.js"></script><script type="text/javascript" src="{pigcms{$static_public}js/artdialog/iframeTools.js"></script><script>var url = "{pigcms{$config.site_url}"var export_url = "{pigcms{:U('Bill/express_statistics_export')}"</script><script type="text/javascript" src="{pigcms{$static_public}js/export.js"> </script><include file="Public:footer"/>