<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Bill/merchant_withdraw')}"   <if condition="empty($_GET['withdraw_status'])">class="on"</if> >商家余额存入记录</a>					<a href="{pigcms{:U('Bill/merchant_withdraw',array('withdraw_status'=>1))}" <if condition="$_GET['withdraw_status'] eq 1">class="on"</if> >待提现商家</a>					<a href="{pigcms{:U('Bill/merchant_withdraw_list')}">已提现记录</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Bill/merchant_withdraw')}" method="get">							<input type="hidden" name="c" value="Bill"/>							<input type="hidden" name="a" value="merchant_withdraw"/>							<if condition="isset($_GET['withdraw_status'])">							<input type="hidden" name="withdraw_status" value="{pigcms{$_GET['withdraw_status']}"/>							</if>							筛选: 							<if condition="$admin_area neq 3">							<div id="choose_pca" province_idss="{pigcms{$_GET.province_idss}" city_idss="{pigcms{$_GET.city_idss}" area_id="{pigcms{$_GET.area_id}" style="display:inline"></div>							</if>							<div style="display:inline">							<input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="name" <if condition="$_GET['searchtype'] eq 'name'">selected="selected"</if>>商户名称</option>																<option value="phone" <if condition="$_GET['searchtype'] eq 'phone'">selected="selected"</if>>联系电话</option>								<option value="mer_id" <if condition="$_GET['searchtype'] eq 'mer_id'">selected="selected"</if>>商家编号</option>							</select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;							</div>							<input type="submit" value="查询" class="button"/>							&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>平台商户总余额：<if condition="$all_money">{pigcms{$all_money}<else />0.00</if></b>													</form>					</td>								<if condition="!isset($_GET['withdraw_status'])">					<td>								<a href="{pigcms{:U('Bill/merchant_withdraw',$_GET)}&export=1" class="button" style="float:right;margin-right: 10px;">导出余额记录</a>						<!--a href="javascript:void(0)" onclick="exports()" class="button" style="float:right;margin-right: 10px;">导出余额记录</a-->									</td>						</if>				</tr>			</table>						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="100" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>商户名称</th>								<if condition="!isset($config['not_o2o_demo'])">								<th>联系电话</th>								</if>								<if condition="$_GET['withdraw_status'] eq 1">									<th>提现时间</th>								</if>								<th>最近待兑现</th>								<th>商家余额</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="$mer_withdraw_list">								<volist name="mer_withdraw_list" id="vo">									<tr>										<td>{pigcms{$vo.mer_id}</td>										<td>{pigcms{$vo.name}</td>										<if condition="!isset($config['not_o2o_demo'])">										<td>{pigcms{$vo.phone}</td>										</if>										<if condition="$_GET['withdraw_status'] eq 1">											<td>{pigcms{$vo.withdraw_time|date="Y-m-d H:i:s",###}</td>										</if>										<td>{pigcms{$vo['withdraw_money']/100}元</td>										<td>{pigcms{$vo.money}</td>																				<td class="textcenter"><a href="{pigcms{:U('Bill/merchant_money_list',array('mer_id'=>$vo['mer_id']))}" >查看</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>		<script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script><script>		var url = "{pigcms{$config.site_url}"    var export_url = "{pigcms{:U('Bill/merchent_withdraw_export')}"</script><script type="text/javascript" src="{pigcms{$static_public}js/export.js"> </script><include file="Public:footer"/>