<include file="Public:header"/><style>.mainnav_title ul a {    padding: 15px 20px;}ul, ol {    margin-bottom: 15px;}.mainnav_title div{	color:#7EBAEF;	float:right;}.mainnav_title a.on div{	color:#C1BEBE;}.info{	line-height:30px;}.table-list thead th {    text-align: center;}.table-list tbody td {    text-align: center;}button{	    margin: 5px;	padding: 6px;	background-color: rgba(255, 255, 255, 0);	box-sizing: border-box;	border-width: 1px;	border-style: solid;	border-color: rgba(121, 121, 121, 1);	border-radius: 2px;	-moz-box-shadow: none;	-webkit-box-shadow: none;	box-shadow: none;	font-size: 16px;	color: #666666;	cursor: pointer ;}</style>		<div class="mainbox">		<div id="nav" class="mainnav_title" style="margin-top: 12px;">			<a href="{pigcms{:U('Bill/merchant_withdraw')}">上一页</a>		</div>			<div style="margin:15px 0;">				<b>商家ID：</b>{pigcms{$now_merchant.mer_id}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>商家名称：</b>{pigcms{$now_merchant.name}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>联系电话：</b>{pigcms{$now_merchant.phone}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;商家余额：</b>{pigcms{$now_merchant.money}<br/><br/>			</div>			<div id="nav" class="mainnav_title" style="margin-top:5px;">				<ul>					<a href="{pigcms{:U('Bill/merchant_money_list',array('mer_id'=>$mer_id))}">商家余额存入记录</a>					<a href="{pigcms{:U('Bill/withdraw_info',array('mer_id'=>$mer_id,'status'=>0))}" <if condition="empty($status) OR $status eq 0">class="on"</if>>待兑现记录</a>					<a href="{pigcms{:U('Bill/withdraw_info',array('mer_id'=>$mer_id,'status'=>1))}" <if condition=" $status eq 1">class="on"</if>>已兑现记录</a>				</ul>			</div>				<div class="mainnav_title">				<if condition="$status eq 1">					<form id="date" method="post" action="{pigcms{:U('Bill/withdraw_info')}" >						<input type="hidden" name="mer_id" value="{pigcms{$mer_id}">						<input type="hidden" name="status" value="{pigcms{$status}">						<font color="#000">日期筛选：</font>						<input type="text" class="input-text" name="begin_time" style="width:120px;" id="d4311"  value="{pigcms{$begin_time}" onfocus="WdatePicker({isShowClear:false,readOnly:true,dateFmt:'yyyy-MM-dd'})"/>			   						<input type="text" class="input-text" name="end_time" style="width:120px;" id="d4311" value="{pigcms{$end_time}" onfocus="WdatePicker({isShowClear:false,readOnly:true,dateFmt:'yyyy-MM-dd'})"/>												提现至：						<select name="withdraw_type">							<option value="-1" <if condition="$withdraw_type eq '-1'">selected="selected"</if>>全部</option>															<option value="2" <if condition="$withdraw_type eq '2'">selected="selected"</if>>微信</option>															<option value="1" <if condition="$withdraw_type eq '1'">selected="selected"</if>>支付宝</option>							<option value="0" <if condition="$withdraw_type eq '0'">selected="selected"</if>>银行卡</option>						</select>						<input type="submit" value="筛选" class="button">					</form>					</if>					</div>							<input type="hidden" id="com_pay_money"name="money" value="">				<input type="hidden" name="mer_id" value="{pigcms{$mer_id}">				<input type="hidden" name="pay_type" value="{pigcms{$type}">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col width="200"/>							<col />							<col/>						</colgroup>						<thead>							<tr>								<th>提现时间</th>								<th>姓名</th>								<th>提现总金额</th>								<th>提现至</th>								<th>账号明细</th>													<th>备注</th>								<if condition="$status eq 0">								<th>操作</th>								<else />								<th>支付方式</th>								</if>							</tr>						</thead>						<tbody>								<if condition="$un_withdraw_list">									<volist name="un_withdraw_list" id="vo">										<tr>											<td>{pigcms{$vo.withdraw_time|date="Y/m/d H:i",###}</td>											<td>{pigcms{$vo.name}</td>											<td>{pigcms{$vo['money']/100}元</td>											<td>{pigcms{$vo.pay_type}</td>																						<td><if condition="$vo['account']">{pigcms{$vo['account']}<php>if($vo['account_detail']){</php>|{pigcms{$vo['account_detail']}<php>}</php></if></td>											<td>{pigcms{$vo.remark}</td>												<if condition="$status eq 0">											<td>												<a href="javascript:void(0);" onclick="window.top.artiframe('/admin.php?g=System&c=Bill&a=edit_withdraw&id={pigcms{$vo.id}&mer_id={pigcms{$mer_id}','编辑',500,300,true,false,false,false,'order_edit',true);"><button>设为已兑现</button></a>												<a href="javascript:void(0);" onclick="window.top.artiframe('/admin.php?g=System&c=Bill&a=edit_reason&id={pigcms{$vo.id}&mer_id={pigcms{$mer_id}','编辑',400,200,true,false,false,false,'order_edit',true);"><button>驳回</button></a>											</td>												<else />												<td><if condition="$vo.online eq 1">企业付款<else />线下付款</if></td>												</if>										</tr>									</volist>									<tr><td class="textcenter pagebar" colspan="7">{pigcms{$pagebar}</td></tr>									<else/>									<tr class="odd"><td class="textcenter red" colspan="7" >暂时还没有提现记录。</td></tr>								</if>						</tbody>					</table>				</div>				</div><script type="text/javascript" src="{pigcms{$static_public}js/artdialog/jquery.artDialog.js"></script><script type="text/javascript" src="{pigcms{$static_public}js/artdialog/iframeTools.js"></script><include file="Public:footer"/>