<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Bus/bus_line')}" class="on">公交线列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Bus/bus_line_add')}','添加公交线',600,300,true,false,false,addbtn,'add',true);">添加公交线</a>|					<a href="{pigcms{:U('Bus/bus_station')}">公交站列表</a>									</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>名称</th>								<th>状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($lineList)">								<volist name="lineList" id="vo">									<tr>										<td>{pigcms{$vo.line_id}</td>										<td>{pigcms{$vo.line_name}</td>														<td>											<if condition="$vo['status'] eq 2"><font color="green">禁止</font><elseif condition="$vo['status'] eq 1"/><font color="red">正常</font></if>										</td>										<td class="textcenter">											<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('bus_line_save',array('line_id'=>$vo['line_id']))}','编辑公交路线信息',600,300,true,false,false,editbtn,'edit',true);">编辑</a>|											<a href="javascript:void(0);" class="delete_row" parameter="line_id={pigcms{$vo.line_id}" url="{pigcms{:U('line_del')}">删除</a>									  	</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="7">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="4">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>