<include file="Public:header"/><style>	.station{width: 150px; height: 40px; float: left;}</style><form id="myform" method="post" action="{pigcms{:U('Bus/bus_line_add_data')}" >	<table cellpadding="0" cellspacing="0" class="frame_form" width="100%">		<tr>			<th width="80">站点所在地</th>			<td id="choose_cityarea" area_id="-1" circle_id="-1"></td>		</tr>		<tr>			<td width="80">线路名称</td>			<td>				<input type="text" class="input fl" name="line_name" value="" placeholder="请填写一个线路名" validate="required:true"></td>		</tr><!-- 		<tr>			<td width="80">是否推荐热门</td>			<td class="radio_box">				<span class="cb-enable">					<label class="cb-enable selected">						<span>不推荐</span>						<input type="radio" name="is_hot" value="1" checked="checked"></label>				</span>								<span class="cb-disable">					<label class="cb-disable">						<span>推荐</span>						<input type="radio" name="is_hot" value="2"></label>				</span>			</td>		</tr> -->		<tr>			<td width="80">状态</td>			<td class="radio_box">				<span class="cb-enable">					<label class="cb-enable selected">						<span>正常</span>						<input type="radio" name="status" value="1" checked="checked"></label>				</span>				<span class="cb-disable">					<label class="cb-disable">						<span>禁止</span>						<input type="radio" name="status" value="2"></label>				</span>			</td>		</tr>		<tr class="hidden" id="stationHidden">			<td width="80">所选站点</td>			<td id="stationVal">			</td>		</tr>		<tr>			<td width="80">选择公交站：</td>			<td>				<input type="text" class="input" id="searchKeyUp" name=""  value="" placeholder="请填写一个站点名称"> 				<!-- <a class="button" id="search">搜索</a>		 -->				<select id="stationSelect" class="">					<option value='xz'>== 请选择站点 ==</option>					<option value='tj'>添加站点</option>				</select>				<!-- <a class="button hidden" id="choiceStation">选择</a> -->			</td>		</tr>	</table>	<script>	var stationUrl = "{pigcms{:U('Bus/bus_station_data')}";		$("#search").click(function(){			$("#stationSelect").removeClass('hidden')			$("#choiceStation").removeClass('hidden')		});		$("#stationSelect").change(function(){			var stationId = $("#stationSelect").val();			var stationName = $("#stationSelect option:selected").text();			if(!isNaN(stationId)){				var stationIdVal = new Array();				var i =1;				$(".stationIdVal").each(function(){					if($(this).val() == stationId){						i++;					}					stationIdVal.push($(this).val());				})				// alert(stationIdVal);				if(i>1){					window.top.msg(0,'已经添加到列表了',true);				}else{					$("#stationHidden").removeClass('hidden');					var html = "<div class='station'><input type='checkbox' class='stationIdVal' name='station["+stationId+"][stationId]' value='"+stationId+"' checked='checked'>"+stationName+"&nbsp;&nbsp;<input type='text' name='station["+stationId+"][sort]' value='0' class='input' style=' width:25px;'></div>";					$("#stationVal").append(html);				} 			}else if( stationId == 'tj'){			   window.top.artiframe('/admin.php?g=System&c=Bus&a=bus_station_add','添加公交站',600,500,true,false,false,addbtn,'store_add',true);			}					})	var flag;		$('#searchKeyUp').keyup(function(){			clearTimeout(flag);	        flag = setTimeout(function(){	        	var name = $("#searchKeyUp").val();	        	if(name){	        		$.post(stationUrl,{'name':name},function(data){					// alert(data.data);					$("#stationSelect").empty(); 					$("#stationSelect").removeClass('hidden');					$("#stationSelect").append(data.data); 									},'json');	        	}					      	}, 500);		});		$("#choiceStation").click(function(){			var stationId = $("#stationSelect").val();			var stationName = $("#stationSelect option:selected").text();			$("#stationHidden").removeClass('hidden');			var html = "<div class='station'><input type='checkbox' name='station[]'' value='"+stationId+"'' checked='checked'>"+stationName+"</div>";			$("#stationVal").append(html);		});		function addStationCallBack(obj){			window.top.msg(1,'添加站点成功',true);			var stationId = obj.id;			var stationName = obj.station_name;			$("#stationHidden").removeClass('hidden');			var html = "<div class='station'><input type='checkbox' class='stationIdVal' name='station["+stationId+"][stationId]' value='"+stationId+"' checked='checked'>"+stationName+"&nbsp;&nbsp;<input type='text' name='station["+stationId+"][sort]' value='0' class='input' style=' width:25px;'></div>";			$("#stationVal").append(html);		}	</script>	<div class="btn hidden">		<input type="submit" name="dosubmit" id="dosubmit" value="提交" class="button" />		<input type="reset" value="取消" class="button" />	</div></form><include file="Public:footer"/>