<include file="Public:header"/><form id="myform" method="post" frame="true" action="{pigcms{:U('Bus/bus_station_add_data')}" data-call_fun="true">	<table cellpadding="0" cellspacing="0" class="frame_form" width="100%">		<tr>			<td width="80">站点名称</td>			<td>				<input type="text" class="input fl" name="station_name" value="" placeholder="请填写一个站台名" validate="required:true"></td>		</tr>		<tr>			<th width="80">站点经纬度</th>			<td id="choose_map"></td>		</tr>		<tr>			<th width="80">站点所在地</th>			<td id="choose_cityarea" area_id="-1" circle="-1"></td>		</tr>		<tr>			<td width="80">首字母</td>			<td>				<input type="text" class="input fl" name="first_letter" value="" placeholder="请填写站点首字母" validate="required:true"></td>		</tr>		<!-- <tr>			<td width="80">是否推荐热门</td>			<td class="radio_box">				<span class="cb-enable">					<label class="cb-enable selected">						<span>不推荐</span>						<input type="radio" name="is_hot" value="1" checked="checked"></label>				</span>				<span class="cb-disable">					<label class="cb-disable">						<span>推荐</span>						<input type="radio" name="is_hot" value="2"></label>				</span>			</td>		</tr> -->		<tr>			<td width="80">状态</td>			<td class="radio_box">				<span class="cb-enable">					<label class="cb-enable selected">						<span>正常</span>						<input type="radio" name="status" value="1" checked="checked"></label>				</span>				<span class="cb-disable">					<label class="cb-disable">						<span>禁止</span>						<input type="radio" name="status" value="2"></label>				</span>			</td>		</tr>	</table>	<div class="btn hidden">		<input type="submit" name="dosubmit" id="dosubmit" value="提交" class="button" />		<input type="reset" value="取消" class="button" />	</div></form><include file="Public:footer"/><script>	var v = "{pigcms{$_GET['v']}";	function submitCallBack(obj){		// alert(obj)		window.top.closeiframebyid('form_submit_tips');		if(v == 'edit'){			window.top.frames['Openedit'].addStationCallBack(obj);		}else{			window.top.frames['Openadd'].addStationCallBack(obj);		}		window.top.closeiframebyid('store_add');	}</script>