<include file="Public:header"/><form id="myform" method="post" action="{pigcms{:U('bus_station_save_data')}">	<table cellpadding="0" cellspacing="0" class="frame_form" width="100%">		<tr>			<td width="80">站点名称</td>			<td>				<input type="text" class="input fl" name="station_name" value="{pigcms{$stationInfo.station_name}" placeholder="请填写一个站台名" validate="required:true"></td>		</tr>		<tr>			<th width="80">站点经纬度</th>			<td id="choose_map" default_long_lat="{pigcms{$stationInfo.long},{pigcms{$stationInfo.lat}"></td>		</tr>		<tr>			<th width="80">城市</th>			<td id="choose_cityarea" circle_id="-1" area_id="-1" province_id="{pigcms{$stationInfo.province_id}" city_id="{pigcms{$stationInfo.city_id}"></td>		</tr>		<tr>			<td width="80">首字母</td>			<td>				<input type="text" class="input fl" name="first_letter" value="{pigcms{$stationInfo.first_letter}" placeholder="请填写站点首字母" validate="required:true"></td>		</tr>		<tr>			<td width="80">状态</td>			<td class="radio_box">				<span class="cb-enable">					<label class="cb-enable <if condition="$stationInfo['status'] eq 1">selected</if>">						<span>正常</span>						<input type="radio" name="status" value="1" <if condition="$stationInfo['status'] eq 1">checked="checked"</if>></label>				</span>				<span class="cb-disable">					<label class="cb-disable <if condition="$stationInfo['status'] eq 2">selected</if>">						<span>禁止</span>						<input type="radio" name="status" value="2" <if condition="$stationInfo['status'] eq 2">checked="checked"</if>></label>				</span>			</td>		</tr>	</table>	<input type="hidden" name="station_id" value="{pigcms{$stationInfo.station_id}">	<div class="btn hidden">		<input type="submit" name="dosubmit" id="dosubmit" value="提交" class="button" />		<input type="reset" value="取消" class="button" />	</div></form><include file="Public:footer"/>