<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('index')}" class="on">银行卡BIN列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('add')}','添加银行卡BIN',500,200,true,false,false,addbtn,'add',true);">添加银行卡BIN</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup><col> <col> <col> <col><col> <col width="140" align="center"> </colgroup>						<thead>							<tr>								<th>排序</th>								<th>银行名称</th>								<th>银行卡名称</th>								<th>银行卡BIN</th>								<th>银行卡类型</th>								<th>编辑时间</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($bank_list)">								<volist name="bank_list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.bankname}</td>										<td>{pigcms{$vo.cardname}</td>										<td>{pigcms{$vo.cardbin}</td>										<td>											<if condition="$vo['cardtype'] eq 1">												储蓄卡											<else/>												信用卡											</if>										</td>										<td>{pigcms{$vo.add_time|date='Y-m-d H:i:s',###}</td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('edit',array('id'=>$vo['id'],'frame_show'=>true))}','查看详细信息',500,200,true,false,false,false,'add',true);">查看</a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('edit',array('id'=>$vo['id']))}','编辑银行卡BIN',500,200,true,false,false,editbtn,'add',true);">编辑</a> | <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('del')}">删除</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="8">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="8">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>