<include file="Public:header"/><form id="myform" method="post" action="" enctype="multipart/form-data">    <input type="hidden" name="fid" value="{pigcms{$_GET.fid}"/>    <if condition='$_GET["fid"]'>         <input type="hidden" name="subdir" value="2"/>    </if>    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <th width="80">分类名称</th>            <td><input type="text" class="input fl" name="cat_name" id="type_name"  size="25" placeholder="" validate="maxlength:20,required:true" tips=""/></td>        </tr>        <tr>            <th width="80">分类LOGO图标</th>            <td><input type="file" class="input fl" name="cat_pic" style="width:200px;" placeholder="分类LOGO图片" <if condition='!$_GET["fid"]'>validate="required:true"</if> tips="分类LOGO小图标，建议为透明白色 尺寸为100*100 的图标"/></td>        </tr>        <if condition='!$_GET["fid"]'>            <tr>                <th width="80">分类选中LOGO图标</th>                <td><input type="file" class="input fl" name="cat_select_pic" style="width:200px;" placeholder="分类LOGO图片" <if condition='!$_GET["fid"]'>validate="required:true"</if> tips="分类选中LOGO小图标，建议为透明白色 尺寸为100*100 的图标"/></td>            </tr>        </if>        <tr>            <th width="80">分类排序</th>            <td><input type="text" class="input fl" name="cat_sort" value="0" size="10" placeholder="分类排序" validate="maxlength:6,required:true,number:true" tips="默认添加时间排序！手动排序数值越大，排序越前。"/></td>        </tr>        <tr>            <th width="80">选中后背景色</th>             <td><input type="text" class="input fl" name="cat_select_color" id="choose_color" style="width:120px;" placeholder="可不填写" tips="请点击右侧按钮选择颜色，用途为页面显示。"/>&nbsp;&nbsp;<a href="javascript:void(0);" id="choose_color_box" style="line-height:28px;">点击选择颜色</a></td>        </tr>        <tr>            <th width="80">是否热门</th>            <td>                <span class="cb-enable"><label class="cb-enable"><span>是</span><input type="radio" name="is_hot" value="2" /></label></span>                <span class="cb-disable"><label class="cb-disable selected"><span>否</span><input type="radio" name="is_hot" value="1" checked="checked" /></label></span>                <em class="notice_tips" tips="如果选择热门，颜色会有变化"></em>            </td>        </tr>        <tr>            <th width="80">分类状态</th>            <td>                <span class="cb-enable"><label class="cb-enable selected"><span>启用</span><input type="radio" name="cat_status" value="1" checked="checked" /></label></span>                <span class="cb-disable"><label class="cb-disable"><span>关闭</span><input type="radio" name="cat_status" value="0" /></label></span>            </td>        </tr>    </table>    <div class="btn hidden">        <input type="submit" name="dosubmit" id="dosubmit" value="提交" class="button" />        <input type="reset" value="取消" class="button" />    </div></form><include file="Public:footer"/>