<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>                    <a href="{pigcms{:U('Community/index')}">社群列表</a>|                    <a href="{pigcms{:U('Community/album_list',array('community_id'=>$_GET['community_id']))}"  class="on">相册列表</a>                </ul>			</div>            <table class="search_table" width="100%">                <tr>                    <td style="width:60%;">                        <form action="{pigcms{:U('Community/album_list')}" method="get">                            <input type="hidden" name="c" value="Community"/>                            <input type="hidden" name="a" value="album_list"/>                            <input type="hidden" name="community_id" value="{pigcms{$_GET['community_id']}"/>                            相册名称:                            <input type="text" name="album_name" class="input-text" value="{pigcms{$_GET['album_name']}"/>                            <input type="submit" value="查询" class="button"/>                        </form>                    </td>                </tr>            </table>						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="100" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>                                <th>相册名称</th>                                <th>创建者</th>                                <th>状态</th>                                <th>图片数</th>                                <th>查看图片</th>                                <th>添加时间</th>                                <th>开启/禁止</th>                                <th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="$community_album">								<volist name="community_album" id="vo">									<tr>										<td>{pigcms{$vo.album_id}</td>                                        <td>{pigcms{$vo.album_name}</td>                                        <td>{pigcms{$vo.nickname}</td>                                        <td>                                            <if condition="$vo['album_status'] eq 1">                                                开启                                                <else/>                                                禁止                                            </if>                                        </td>                                        <td>{pigcms{$vo.pic_num}</td>                                        <td><a href="{pigcms{:U('Community/album_img_list',array('album_id'=>$vo['album_id'], 'community_id'=>$_GET['community_id']))}">查看</a></td>                                        <td>{pigcms{$vo.add_time|date='Y-m-d H:i:s',###}</td>                                        <td>                                            <span class="cb-enable"><label class="cb-enable <if condition="$vo['album_status'] eq 1">selected</if>"><span onclick="albumSwitch('{pigcms{$vo.album_id}', 1)" >开启</span><input type="radio" value="1" <if condition="$vo['album_status'] eq 1">checked="checked"</if> /></label></span>                                            <span class="cb-disable"><label class="cb-disable <if condition="$vo['album_status'] eq 2">selected</if>"><span onclick="albumSwitch('{pigcms{$vo.album_id}', 2)">禁止</span><input type="radio" value="2" <if condition="$vo['album_status'] eq 2">checked="checked"</if> /></label></span>                                        </td>                                        <td class="textcenter">                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Community/look_album',array('album_id'=>$vo['album_id']))}','查看信息',700,500,true,false,false,false,'detail',true);" >查看</a>                                        </td>                                    </tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$page}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>		<script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script>        <script>            var lockInfo = false;            function albumSwitch(album_id, status) {                if (lockInfo) return;                lockInfo = true;                var url = '';                var close_album = "{pigcms{:U('Community/close_album')}";                var open_album = "{pigcms{:U('Community/open_album')}";                if (1 == status) {                    url = open_album;                } else if(2 == status) {                    url = close_album;                } else {                    return;                }                $.post(url,{'album_id': album_id},function(data){                    console.log(data)                    if (data && (data.status === 1 || data.status === '1')) {                        location.reload();                    } else {                        alert(data.info)                    }                },'json')                console.log('数据信息', type_id, status)                setTimeout(function(){                    lockInfo = false;                },1000);            }        </script><include file="Public:footer"/>