<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>                    <if condition="empty($now_category)">                        <a href="{pigcms{:U('Community/category')}"  class="on">分类列表</a>|                        <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Community/add_category',array('fid'=>intval($_GET['fid'])))}','添加分类',700,480,true,false,false,addbtn,'add',true);">添加分类</a>                        <else/>                        <a href="{pigcms{:U('Community/category')}">分类列表</a>|                        <a href="{pigcms{:U('Community/category',array('fid'=>$_GET['fid']))}" class="on">{pigcms{$now_category.cat_name} - 子分类列表</a>|                        <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Community/add_category',array('fid'=>intval($_GET['fid'])))}','添加子分类',700,480,true,false,false,addbtn,'add',true);">添加子分类</a>                    </if>                </ul>			</div>						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="100" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>                                <th>类型名称</th>                                <th>状态</th>                                <if condition="empty($_GET['fid'])">                                    <th>查看子分类</th>                                </if>                                <th>添加时间</th>                                <th>开启/禁止</th>                                <th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="$community_category">								<volist name="community_category" id="vo">									<tr>										<td>{pigcms{$vo.cid}</td>                                        <td><if condition="$vo['is_hot'] eq 2"><font color="red">{pigcms{$vo.cat_name}</font><else/>{pigcms{$vo.cat_name}</if></td>                                        <td>                                            <if condition="$vo['cat_status'] eq 1">                                                开启                                                <else/>                                                禁止                                            </if>                                        </td>                                        <if condition="empty($_GET['fid'])">                                            <td><a href="{pigcms{:U('Community/category',array('fid'=>$vo['cid']))}">查看子分类</a></td>                                        </if>                                        <td>{pigcms{$vo.add_time|date='Y-m-d H:i:s',###}</td>                                        <td>                                            <span class="cb-enable"><label class="cb-enable <if condition="$vo['cat_status'] eq 1">selected</if>"><span onclick="discoverSwitch('{pigcms{$vo.cid}', 1)" >开启</span><input type="radio" value="1" <if condition="$vo['cat_status'] eq 1">checked="checked"</if> /></label></span>                                            <span class="cb-disable"><label class="cb-disable <if condition="$vo['cat_status'] eq 2">selected</if>"><span onclick="discoverSwitch('{pigcms{$vo.cid}', 2)">禁止</span><input type="radio" value="2" <if condition="$vo['cat_status'] eq 2">checked="checked"</if> /></label></span>                                        </td>                                        <td class="textcenter">                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Community/edit_category',array('cid'=>$vo['cid'], 'fid'=>intval($_GET['fid']), 'frame_show'=>true))}','查看分类信息',700,480,true,false,false,false,'detail',true);">查看</a>                                            | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Community/edit_category',array('cid'=>$vo['cid'], 'fid'=>intval($_GET['fid'])))}','编辑分类信息',700,480,true,false,false,editbtn,'add',true);" >编辑</a>                                            | <a href="javascript:void(0);"  onclick='del_category("{pigcms{$vo.cid}")'>删除</a>                                        </td>                                    </tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>        <style>            .layui-layer-dialog {                top: 300px !important;            }        </style>		<script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script>        <script>            var lockInfo = false;            function del_category(cid){                if (lockInfo) return;                lockInfo = true;                layer.open({                    title:['提示','background-color:#FF658E;color:#fff;'],                    content:'是否确认删除',                    shadeClose:false,                    btn: ['确定', '取消'],                    yes:function(){                        var del_category = "{pigcms{:U('Community/del_category')}";                        $.post(del_category,{'cid':cid},function(data){                            console.log(data)                            if (data && (data.status === 1 || data.status === '1')) {                                location.reload();                            } else {                                alert(data.info)                            }                        },'json')                        setTimeout(function(){                            lockInfo = false;                        },1000);                    }                });                setTimeout(function(){                    lockInfo = false;                },1000);            }            function discoverSwitch(cid, status) {                if (lockInfo) return;                lockInfo = true;                var url = '';                var close_category = "{pigcms{:U('Community/close_category')}";                var open_category = "{pigcms{:U('Community/open_category')}";                if (1 == status) {                    url = open_category;                } else if(2 == status) {                    url = close_category;                } else {                    return;                }                $.post(url,{'cid': cid},function(data){                    console.log(data)                    if (data && (data.status === 1 || data.status === '1')) {                        location.reload();                    } else {                        alert(data.info)                    }                },'json')                console.log('数据信息', type_id, status)                setTimeout(function(){                    lockInfo = false;                },1000);            }        </script><include file="Public:footer"/>