<include file="Public:header"/><div class="mainbox">    <div id="nav" class="mainnav_title">        <ul>            <a href="{pigcms{:U('Community/index')}">社群列表</a>|            <a href="{pigcms{:U('Community/comm_card_list',array('community_id'=>$_GET['community_id']))}"  class="on">{pigcms{$community_info.community_name} - 名片列表</a>        </ul>    </div>    <style>        .table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}    </style>    <table class="search_table" width="100%">        <tr>            <td style="width:60%;">                <form action="{pigcms{:U('Community/comm_card_list')}" method="get">                    <input type="hidden" name="c" value="Community"/>                    <input type="hidden" name="a" value="comm_card_list"/>                    <input type="hidden" name="community_id" value="{pigcms{$_GET['community_id']}"/>                    加入者姓名:                    <input type="text" name="nickname" class="input-text" value="{pigcms{$_GET['nickname']}"/>                    <input type="submit" value="查询" class="button"/>                </form>            </td>        </tr>    </table>    <if condition="$community_card">        <form name="myform" id="myform" action="" method="post">            <div class="table-list" style="margin-bottom: 45px;">            <div style="text-align: center; line-height:25px;padding-top:5px;padding-bottom:5px; font-size: 18px;">{pigcms{$community_info.community_name} -【{pigcms{$community_card.card_name}】名片信息</div>              <table width="100%" cellspacing="0">                <colgroup>                    <col/>                    <col/>                    <col/>                    <col/>                    <col/>                    <col/>                    <col width="100" align="center"/>                </colgroup>                <thead>                <tr>                    <th>名片名称</th>                    <th>名片背景图(以下为强制小图，点击图片查看大图)</th>                    <th>名片成员数</th>                    <th>浏览数</th>                    <th>简介</th>                    <th>添加时间</th>                    <th class="textcenter">操作</th>                </tr>                </thead>                <tbody>                <tr>                    <td>{pigcms{$community_card.card_name}</td>                    <td>                        <img src="{pigcms{$config.site_url}{pigcms{$community_card.community_card_bgimg}" style="width:300px;height:80px;" class="view_msg"/>                    </td>                    <td>{pigcms{$community_card.join_num}</td>                    <td>{pigcms{$community_card.browse_num}</td>                    <td>{pigcms{$community_card.community_card_des|msubstr=###,0,50}</td>                    <td>{pigcms{$community_card.add_time|date='Y-m-d H:i:s',###}</td>                    <td class="textcenter">                        <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Community/look_card',array('community_card_id'=>$community_card['community_card_id']))}','查看信息',700,650,true,false,false,false,'detail',true);" >查看</a>                    </td>                </tr>                </tbody>              </table>           </div>        </form>    </if>    <form name="myform" id="myform" action="" method="post">        <div class="table-list">            <div style="text-align: center; line-height:25px;padding-top:5px;padding-bottom:5px; font-size: 18px;">加入者名片信息列表</div>            <table width="100%" cellspacing="0">                <colgroup>                    <col/>                    <col/>                    <col/>                    <col/>                    <col/>                    <col/>                    <col width="100" align="center"/>                </colgroup>                <thead>                <tr>                    <th>编号</th>                    <th>加入者姓名</th>                    <th>状态</th>                    <th>手机号</th>                    <th>头像(以下为强制小图，点击图片查看大图)</th>                    <th>名片背景图(以下为强制小图，点击图片查看大图)</th>                    <th>添加时间</th>                    <th class="textcenter">操作</th>                </tr>                </thead>                <tbody>                <if condition="$join_info">                    <volist name="join_info" id="vo">                        <tr>                            <td>{pigcms{$vo.join_id}</td>                            <td>                                <if condition="$vo['nickname']">                                    {pigcms{$vo.nickname}                                    <else/>                                    {pigcms{$vo.user_nickname}                                </if>                            </td>                            <td>                                <if condition="$vo['join_status'] eq 1">                                    开启                                    <else/>                                    删除                                </if>                            </td>                            <td>{pigcms{$vo.phone}</td>                            <td>                                <img src="{pigcms{$config.site_url}{pigcms{$vo.join_avatar}" style="width:300px;height:80px;" class="view_msg"/>                            </td>                            <td>                                <img src="{pigcms{$config.site_url}{pigcms{$vo.join_bgimg}" style="width:300px;height:80px;" class="view_msg"/>                            </td>                            <td>{pigcms{$vo.join_time|date='Y-m-d H:i:s',###}</td>                            <td class="textcenter">                                <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Community/look_card_join',array('join_id'=>$vo['join_id']))}','查看信息',700,500,true,false,false,false,'detail',true);" >查看</a>                            </td>                        </tr>                    </volist>                    <tr><td class="textcenter pagebar" colspan="11">{pigcms{$page}</td></tr>                    <else/>                    <tr><td class="textcenter red" colspan="11">列表为空！</td></tr>                </if>                </tbody>            </table>        </div>    </form></div><include file="Public:footer"/>