<include file="Public:header"/><form id="myform" method="post" action="" enctype="multipart/form-data">    <input type="hidden" name="edit" value="1"/>    <input type="hidden" name="cid" value="{pigcms{$_GET.cid}"/>    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <th width="80">分类id</th>            <td>                {pigcms{$info.cid}            </td>        </tr>        <tr>            <th width="80">分类名称</th>            <td><input type="text" class="input fl" name="cat_name" id="cat_name" value="{pigcms{$info.cat_name}" size="25" placeholder="" validate="maxlength:20,required:true" tips=""/></td>        </tr>        <tr>            <th width="80">分类LOGO图标现图</th>            <td><img src="{pigcms{$config.site_url}/upload/system/{pigcms{$info['cat_pic']}" style="width:50px;height:50px;" class="view_msg"/></td>        </tr>        <tr>            <th width="80">分类LOGO图标</th>            <td><input type="file" class="input fl" name="cat_pic" style="width:200px;" placeholder="分类LOGO图片" tips="分类LOGO小图标，建议为透明白色 尺寸为100*100 的图标"/></td>        </tr>        <if condition='!$_GET["fid"]'>            <tr>                <th width="80">分类选中LOGO图标现图</th>                <td><img src="{pigcms{$config.site_url}/upload/system/{pigcms{$info['cat_select_pic']}" style="width:50px;height:50px;" class="view_msg"/></td>            </tr>            <tr>                <th width="80">分类选中LOGO图标</th>                <td><input type="file" class="input fl" name="cat_select_pic"  style="width:200px;" placeholder="分类LOGO图片" tips="分类选中LOGO小图标，建议为透明白色 尺寸为100*100 的图标"/></td>            </tr>        </if>        <tr>            <th width="80">分类排序</th>            <td><input type="text" class="input fl" name="cat_sort" value="{pigcms{$info.cat_sort}" size="10" placeholder="分类排序" validate="maxlength:6,required:true,number:true" tips="默认添加时间排序！手动排序数值越大，排序越前。"/></td>        </tr>        <tr>            <th width="80">选中后背景色</th>             <td><input type="text" class="input fl" name="cat_select_color" value="{pigcms{$info.cat_select_color}" id="choose_color" style="width:120px;" placeholder="可不填写" tips="请点击右侧按钮选择颜色，用途为页面显示。"/>&nbsp;&nbsp;<a href="javascript:void(0);" id="choose_color_box" style="line-height:28px;">点击选择颜色</a></td>        </tr>        <tr>            <th width="80">是否热门</th>            <td>                <span class="cb-enable"><label class="cb-enable <if condition="$info['is_hot'] eq 2">selected</if>"><span>是</span><input type="radio" name="is_hot" value="2" <if condition="$info['is_hot'] eq 2">checked="checked"</if>/></label></span>                <span class="cb-disable"><label class="cb-disable <if condition="$info['is_hot'] eq 1">selected</if>"><span>否</span><input type="radio" name="is_hot" value="1"  <if condition="$info['is_hot'] eq 1">checked="checked"</if> /></label></span>                <em class="notice_tips" tips="如果选择热门，颜色会有变化"></em>            </td>        </tr>        <tr>            <th width="80">状态</th>            <td>                <span class="cb-enable"><label class="cb-enable <if condition="$info['cat_status'] eq 1">selected</if>"><span>开启</span><input name="cat_status" type="radio" value="1" <if condition="$info['cat_status'] eq 1">checked="checked"</if> /></label></span>                <span class="cb-disable"><label class="cb-disable <if condition="$info['cat_status'] eq 2">selected</if>"><span>禁止</span><input name="cat_status" type="radio" value="2" <if condition="$info['cat_status'] eq 2">checked="checked"</if> /></label></span>            </td>        </tr>        <tr>            <th width="80">添加时间</th>            <td>                {pigcms{$info.add_time|date='Y-m-d H:i:s',###}            </td>        </tr>    </table>    <div class="btn hidden">        <input type="submit" name="dosubmit" id="dosubmit" value="提交" class="button" />        <input type="reset" value="取消" class="button" />    </div></form><include file="Public:footer"/>