<include file="Public:header"/><form id="myform" method="post" action="" enctype="multipart/form-data">    <input type="hidden" name="topic_cate_id" value="{pigcms{$_GET.topic_cate_id}"/>    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <th width="80">分类id</th>            <td>                {pigcms{$info.topic_cate_id}            </td>        </tr>        <tr>            <th width="80">分类名称</th>            <td><input type="text" class="input fl" name="topic_cate_name" id="topic_cate_name" value="{pigcms{$info.topic_cate_name}" size="25" placeholder="" validate="maxlength:20,required:true" tips=""/></td>        </tr>        <tr>            <th width="80">状态</th>            <td>                <span class="cb-enable"><label class="cb-enable <if condition="$info['topic_cate_is_del'] eq 0">selected</if>"><span>开启</span><input name="topic_cate_is_del" type="radio" value="0" <if condition="$info['topic_cate_is_del'] eq 0">checked="checked"</if> /></label></span>                <span class="cb-disable"><label class="cb-disable <if condition="$info['topic_cate_is_del'] eq 1">selected</if>"><span>禁止</span><input name="topic_cate_is_del" type="radio" value="1" <if condition="$info['topic_cate_is_del'] eq 1">checked="checked"</if> /></label></span>            </td>        </tr>        <tr>            <th width="80">添加时间</th>            <td>                {pigcms{$info.topic_cate_addtime|date='Y-m-d H:i:s',###}            </td>        </tr>    </table>    <div class="btn hidden">        <input type="submit" name="dosubmit" id="dosubmit" value="提交" class="button" />        <input type="reset" value="取消" class="button" />    </div></form><include file="Public:footer"/>