<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>                    <a href="{pigcms{:U('Community/index')}"  class="on">社群列表</a>				</ul>			</div>						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="100" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>                                <th>群名称</th>                                <th>当前群人数</th>                                <th>入群费用</th>                                <th>状态</th>                                <th>分类标签</th>                                <th>添加时间</th>                                <th>群商城绑定功能</th>                                <th>群聊功能</th>                                <th>文件夹</th>                                <th>相册</th>                                <th>名片</th>                                <th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="$community_info">								<volist name="community_info" id="vo">									<tr>										<td>{pigcms{$vo.community_id}</td>                                        <td>{pigcms{$vo.community_name}</td>                                        <td>{pigcms{$vo.member_number}</td>                                        <td>                                            <if condition="$vo['charge_money'] eq 0">                                                免费                                                <else/>                                                {pigcms{$vo.charge_money}                                            </if>                                        </td>                                        <td>                                            <if condition="$vo['status'] eq 1">                                                正常                                                <elseif condition="$vo['status'] eq 2"/>                                                已解散                                                <else/>                                                禁止                                            </if>                                        </td>                                        <td>                                            <if condition="count($vo.category_info) neq 0">                                                <volist name="vo.category_info" id="msg">                                                    <div>                                                        {pigcms{$msg.cat_name_total}&nbsp;                                                    </div>                                                </volist>                                            <else/>                                                <div>                                                    暂无                                                </div>                                            </if>                                        </td>                                        <td>{pigcms{$vo.add_time|date='Y-m-d H:i:s',###}</td>                                        <td>                                            <span class="cb-enable"><label class="cb-enable <if condition="$vo['community_shop_switch'] eq 0">selected</if>"><span onclick="shopSwitch('{pigcms{$vo.community_id}', 0)" >开启</span><input type="radio" value="0" <if condition="$vo['cat_type'] eq 0">checked="checked"</if> /></label></span>                                            <span class="cb-disable"><label class="cb-disable <if condition="$vo['community_shop_switch'] eq 1">selected</if>"><span onclick="shopSwitch('{pigcms{$vo.community_id}', 1)">关闭</span><input type="radio" value="1" <if condition="$vo['cat_type'] eq 1">checked="checked"</if> /></label></span>                                        </td>                                        <td>                                            <span class="cb-enable"><label class="cb-enable <if condition="$vo['community_chat_switch'] eq 0">selected</if>"><span  onclick="chatSwitch('{pigcms{$vo.community_id}', 0)" >开启</span><input type="radio" value="0" <if condition="$vo['cat_type'] eq 0">checked="checked"</if> /></label></span>                                            <span class="cb-disable"><label class="cb-disable <if condition="$vo['community_chat_switch'] eq 1">selected</if>"><span  onclick="chatSwitch('{pigcms{$vo.community_id}', 1)">关闭</span><input type="radio" value="1" <if condition="$vo['cat_type'] eq 1">checked="checked"</if> /></label></span>                                        </td>                                        <td>                                            <a href="{pigcms{:U('Community/folder_list',array('community_id'=>$vo['community_id']))}">查看</a>                                        </td>                                        <td>                                            <a href="{pigcms{:U('Community/album_list',array('community_id'=>$vo['community_id']))}">查看</a>                                        </td>                                        <td>                                            <a href="{pigcms{:U('Community/comm_card_list',array('community_id'=>$vo['community_id']))}">查看</a>                                        </td>                                        <td class="textcenter">                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Community/look_over',array('community_id'=>$vo['community_id']))}','查看社群信息',480,500,true,false,false,false,'detail',true);" >查看</a>                                            |                                            <if condition="$vo['status'] eq 1">                                                <a href="javascript:void(0);"  onclick='banOrEnable("{pigcms{$vo.community_id}")'>禁止</a>                                                <elseif condition="$vo['status'] eq 2"/>                                                已解散                                                <else/>                                                <a href="javascript:void(0);" onclick='banOrEnable("{pigcms{$vo.community_id}")'>启用</a>                                            </if>                                        </td>                                    </tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>        <script>            var lockInfo = false;            function banOrEnable(community_id){                if (lockInfo) return;                lockInfo = true;                var ban_or_enable_url = "{pigcms{:U('Community/ban_group')}";                $.post(ban_or_enable_url,{'community_id':community_id},function(data){                    console.log(data)                    if (data && (data.status === 1 || data.status === '1')) {                        location.reload();                    }                    lockInfo = false;                },'json')            }            function chatSwitch(community_id, status) {                if (lockInfo) return;                lockInfo = true;                var chat_switch_url = "{pigcms{:U('Community/group_chat_switch')}";                $.post(chat_switch_url,{'community_id': community_id, community_chat_switch: status},function(data){                    console.log(data)                    if (data && (data.status === 1 || data.status === '1')) {                        location.reload();                    }                    lockInfo = false;                },'json')               console.log('聊天数据信息', community_id, status)            }            function shopSwitch(community_id, status) {                if (lockInfo) return;                lockInfo = true;                var shop_switch_url = "{pigcms{:U('Community/group_shop_switch')}";                $.post(shop_switch_url,{'community_id': community_id, community_shop_switch: status},function(data){                    console.log(data)                    if (data && (data.status === 1 || data.status === '1')) {                        location.reload();                    }                    lockInfo = false;                },'json')                console.log('商店数据信息', community_id, status)            }        </script><include file="Public:footer"/>