<include file="Public:header"/><form id="myform" method="post" action="" enctype="multipart/form-data">    <input type="hidden" name="cid" value="{pigcms{$now_category['cid']}"/>    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <th width="80">相册id</th>            <td>                {pigcms{$info.album_id}            </td>        </tr>        <tr>            <th width="80">创建者</th>            <td>                {pigcms{$info.nickname}            </td>        </tr>        <tr>            <th width="80">相册名</th>            <td>                {pigcms{$info.album_name}            </td>        </tr>        <tr>            <th width="80">相册下图片数量</th>            <td>                {pigcms{$info.pic_num}            </td>        </tr>        <tr>            <th width="80">相册状态</th>            <td>                <if condition="$info['album_status'] eq 1">                    正常                    <elseif condition="info['album_status'] eq 2"/>                    禁止                    <else/>                    删除                </if>            </td>        </tr>        <tr>            <th width="80">是否允许他人上传图片</th>            <td>                <if condition="$info['other_is_upload'] eq 1">                    否                    <else/>                    是                </if>            </td>        </tr>        <tr>            <th width="80">描述</th>            <td>                {pigcms{$info.album_des}            </td>        </tr>        <tr>            <th width="80">上传时间</th>            <td>                {pigcms{$info.add_time|date='Y-m-d H:i:s',###}            </td>        </tr>    </table></form><include file="Public:footer"/>