<include file="Public:header"/><form id="myform" method="post" action="" enctype="multipart/form-data">    <input type="hidden" name="cid" value="{pigcms{$now_category['cid']}"/>    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <th width="80">图片id</th>            <td>                {pigcms{$info.file_id}            </td>        </tr>        <tr>            <th width="80">创建者</th>            <td>                {pigcms{$info.nickname}            </td>        </tr>        <tr>            <th width="80">图片名</th>            <td>                {pigcms{$info.file_remark}            </td>        </tr>        <tr>            <th width="80">后台随机生成的图片名</th>            <td>                {pigcms{$info.file_name}            </td>        </tr>        <tr>            <th width="80">图片类型</th>            <td>                {pigcms{$info.file_type}            </td>        </tr>        <tr>            <th width="80">图片后缀名</th>            <td>                {pigcms{$info.file_suffix}            </td>        </tr>        <tr>            <th width="80">图片状态</th>            <td>                <if condition="$info['file_status'] eq 1">                    正常                    <elseif condition="info['file_status'] eq 2"/>                    禁止                    <else/>                    删除                </if>            </td>        </tr>        <tr>            <th width="80">图片(右边为强制小图，点击图片查看大图)</th>            <td>                <img src="{pigcms{$info.file_url}" style="width:300px;height:80px;" class="view_msg"/>            </td>        </tr>        <tr>            <th width="80">描述</th>            <td>                {pigcms{$info.file_des}            </td>        </tr>        <tr>            <th width="80">是否同步到了动态</th>            <td>                <if condition="$info['is_add_dynamic'] eq 1">                    否                    <else/>                    是                </if>            </td>        </tr>        <tr>            <th width="80">上传时间</th>            <td>                {pigcms{$info.add_time|date='Y-m-d H:i:s',###}            </td>        </tr>    </table></form><include file="Public:footer"/>