<include file="Public:header"/><form id="myform" method="post" action="" enctype="multipart/form-data">    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <th width="80">名片id</th>            <td>                {pigcms{$info.community_card_id}            </td>        </tr>        <tr>            <th width="80">群名片名称</th>            <td>                {pigcms{$info.card_name}            </td>        </tr>        <tr>            <th width="80">背景图片(右边为强制小图，点击图片查看大图)</th>            <td>                <img src="{pigcms{$info.community_card_bgimg}" style="width:300px;height:80px;" class="view_msg"/>            </td>        </tr>        <tr>            <th width="80">加入名片成员数</th>            <td>                {pigcms{$info.join_num}            </td>        </tr>        <tr>            <th width="80">浏览数</th>            <td>                {pigcms{$info.browse_num}            </td>        </tr>        <tr>            <th width="80">描述</th>            <td>                {pigcms{$info.community_card_des}            </td>        </tr>        <tr>            <th width="80">上传时间</th>            <td>                {pigcms{$info.add_time|date='Y-m-d H:i:s',###}            </td>        </tr>    </table>    <if condition="$info['option']">    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <td  style="100%;text-align: center">填写选项</td>        </tr>        <tr>            <th width="80">选项名称</th>            <th width="80">是否必填</th>            <th width="80">类型</th>            <th width="80">是否隐私</th>            <th width="80">状态</th>            <th width="80">添加时间</th>            <th width="80">同步个人名片字段</th>        </tr>        <volist name="info['option']" id="vo">            <tr>                <td width="80">                    {pigcms{$vo.option_name}                </td>                <td width="80">                    <if condition="$vo['is_required'] eq 1">                        选填                        <else/>                        必填                    </if>                </td>                <td width="80">                    <if condition="$vo['option_type'] eq 1">                        单行文本                        <else/>                        多行文本                    </if>                </td>                <td width="80">                    <if condition="$vo['is_look'] eq 1">                        隐藏                        <else/>                        显示                    </if>                </td>                <td width="80">                    <if condition="$vo['option_status'] eq 1">                        开启                        <else/>                        删除                    </if>                </td>                <td width="80">                    {pigcms{$vo.add_time|date='Y-m-d H:i:s',###}                </td>                <td width="80">                    {pigcms{$vo.default_word}                </td>            </tr>        </volist>    </table>    </if></form><include file="Public:footer"/>