<include file="Public:header"/><form id="myform" method="post" action="" enctype="multipart/form-data">    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <th width="80">名片id</th>            <td>                {pigcms{$info.join}            </td>        </tr>        <tr>            <th width="80">群名片状态</th>            <td>                <if condition="$info['join_status'] eq 1">                    开启                    <else/>                    删除                </if>            </td>        </tr>        <tr>            <th width="80">姓名</th>            <td>                <if condition="$info['nickname']">                    {pigcms{$info.nickname}                    <else/>                    {pigcms{$info.user_nickname}                </if>            </td>        </tr>        <tr>            <th width="80">手机号</th>            <td>{pigcms{$info.phone}</td>        </tr>        <tr>            <th width="80">头像(以下为强制小图，点击图片查看大图)</th>            <td>                <img src="{pigcms{$info.join_avatar}" style="width:300px;height:80px;" class="view_msg"/>            </td>        </tr>        <tr>            <th width="80">名片背景图(以下为强制小图，点击图片查看大图)</th>            <td>                <img src="{pigcms{$info.join_bgimg}" style="width:300px;height:80px;" class="view_msg"/>            </td>        </tr>        <if condition="$info['content']">            <volist name="info.content" id="vo" key="item">                <tr>                    <th width="80">{pigcms{$vo.title}</th>                    <td>                        {pigcms{$vo.value}                    </td>                </tr>            </volist>        </if>        <tr>            <th width="80">上传时间</th>            <td>                {pigcms{$info.join_time|date='Y-m-d H:i:s',###}            </td>        </tr>    </table></form><include file="Public:footer"/>