<include file="Public:header"/><form id="myform" method="post" action="" enctype="multipart/form-data">    <input type="hidden" name="cid" value="{pigcms{$now_category['cid']}"/>    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <th width="80">文件id</th>            <td>                {pigcms{$info.file_id}            </td>        </tr>        <tr>            <th width="80">创建者</th>            <td>                {pigcms{$info.nickname}            </td>        </tr>        <tr>            <th width="80">文件名</th>            <td>                {pigcms{$info.file_remark}            </td>        </tr>        <tr>            <th width="80">后台随机生成的文件名</th>            <td>                {pigcms{$info.file_name}            </td>        </tr>        <tr>            <th width="80">文件类型</th>            <td>                {pigcms{$info.file_type}            </td>        </tr>        <tr>            <th width="80">文件后缀名</th>            <td>                {pigcms{$info.file_suffix}            </td>        </tr>        <tr>            <th width="80">文件状态</th>            <td>                <if condition="$info['file_status'] eq 1">                    正常                    <elseif condition="info['file_status'] eq 2"/>                    禁止                    <else/>                    删除                </if>            </td>        </tr>        <tr>            <th width="80">上传时间</th>            <td>                {pigcms{$info.add_time|date='Y-m-d H:i:s',###}            </td>        </tr>        <tr>            <th width="80">点击下载</th>            <td>                <a href="javascript:void(0);" onclick="window.location.href='{pigcms{$info.file_url}';" >点击查看/下载</a>            </td>        </tr>    </table></form><include file="Public:footer"/>