<include file="Public:header"/><form id="myform" method="post" action="" enctype="multipart/form-data">    <input type="hidden" name="cid" value="{pigcms{$now_category['cid']}"/>    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <th width="80">小程序群id</th>            <td>                {pigcms{$community_info.community_id}            </td>        </tr>        <tr>            <th width="80">绑定微信群id</th>            <td>                {pigcms{$community_info.openGId}            </td>        </tr>        <tr>            <th width="80">群名称</th>            <td>                {pigcms{$community_info.community_name}            </td>        </tr>        <tr>            <th width="80">当前群人数</th>            <td>                {pigcms{$community_info.member_number}            </td>        </tr>        <tr>            <th width="80">群状态</th>            <td>                <if condition="$community_info['status'] eq 1">                    正常                    <elseif condition="$community_info['status'] eq 2"/>                    已解散                    <else/>                    禁止                </if>            </td>        </tr>        <tr>            <th width="80">群分类标签</th>            <td>                <if condition="count($community_info.category_info) neq 0">                    <volist name="community_info.category_info" id="msg">                        <div>                            {pigcms{$msg.cat_name_total}&nbsp;                        </div>                    </volist>                    <else/>                    <div>                        暂无                    </div>                </if>            </td>        </tr>        <tr>            <th width="80">入群方式</th>            <td>                <if condition="$community_info['group_mode'] eq 1">                    只允许微信群内成员加群                    <elseif condition="$community_info['group_mode'] eq 2"/>                    允许任何人加群                    <else/>                    不允许任何人加群                </if>            </td>        </tr>        <tr>            <th width="80">是否收费</th>            <td>                <if condition="$community_info['is_charge'] eq 1">                    免费                    <else/>                    收费                </if>            </td>        </tr>        <tr>            <th width="80">费用</th>            <td>                <if condition="$community_info['charge_money'] eq 0">                    免费                    <else/>                    {pigcms{$community_info.charge_money}                </if>            </td>        </tr>        <tr>            <th width="80">是否审核</th>            <td>                <if condition="$community_info['is_check'] eq 1">                    无需审核                    <else/>                    需要审核                </if>            </td>        </tr>        <tr>            <th width="80">群昵称功能</th>            <td>                <if condition="$community_info['is_nickname'] eq 1">                    关闭                    <else/>                    开启                </if>            </td>        </tr>        <tr>            <th width="80">建群时间</th>            <td>                {pigcms{$community_info.add_time|date='Y-m-d H:i:s',###}            </td>        </tr>    </table></form><include file="Public:footer"/>