<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>                    <a href="{pigcms{:U('Community/dynamic_note',array('topic_cate_id'=>$id['topic_cate_id'],'topic_id'=>$id['topic_id']))}">帖子列表</a>|                    <a href=""  class="on">回复列表</a>                </ul>			</div>            <!-- <table class="search_table" width="100%">                <tr>                    <td style="width:60%;">                        <form action="{pigcms{:U('Community/album_list')}" method="get">                            <input type="hidden" name="c" value="Community"/>                            <input type="hidden" name="a" value="album_list"/>                            <input type="hidden" name="community_id" value="{pigcms{$_GET['community_id']}"/>                            <input type="text" name="album_name" class="input-text" value="{pigcms{$_GET['album_name']}"/>                            <input type="submit" value="查询" class="button"/>                        </form>                    </td>                </tr>            </table> -->						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="100" align="center"/>						</colgroup>						<thead>							<tr>								<th>回复编号</th>                                <th>内容</th>                                <th>回复人</th>                                <th>被回复人</th>                                <th>状态</th>                                <th>开启/禁止</th>                                <th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="$reply_list">								<volist name="reply_list" id="vo">									<tr>										<td>{pigcms{$vo.reply_id}</td>                                        <td>{pigcms{$vo.reply_content}</td>                                        <td>{pigcms{$vo.user}</td>                                        <td>{pigcms{$vo.to_user}</td>                                        <td>                                            <if condition="$vo['reply_is_del'] eq 0">                                                开启                                                <else/>                                                禁止                                            </if>                                        </td>                                        <td>                                            <span class="cb-enable"><label class="cb-enable <if condition="$vo['reply_is_del'] eq 0">selected</if>"><span onclick="albumSwitch('{pigcms{$vo.reply_id}', 0)" >开启</span><input type="radio" value="0" <if condition="$vo['reply_is_del'] eq 0">checked="checked"</if> /></label></span>                                            <span class="cb-disable"><label class="cb-disable <if condition="$vo['reply_is_del'] eq 1">selected</if>"><span onclick="albumSwitch('{pigcms{$vo.reply_id}', 1)">禁止</span><input type="radio" value="1" <if condition="$vo['reply_is_del'] eq 1">checked="checked"</if> /></label></span>                                        </td>                                        <td class="textcenter">                                            <a href="javascript:void(0);"  onclick='del_reply("{pigcms{$vo.reply_id}")'>删除</a>                                        </td>                                    </tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$page}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>		<script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script>        <script>            var lockInfo = false;            function del_reply(reply_id){                if (lockInfo) return;                lockInfo = true;                layer.open({                    title:['提示','background-color:#FF658E;color:#fff;'],                    content:'是否确认删除',                    shadeClose:false,                    offset: ['auto', '700px'],                    btn: ['确定', '取消'],                    yes:function(){                        var del_reply = "{pigcms{:U('Community/del_reply')}";                        $.post(del_reply,{'reply_id':reply_id},function(data){                            console.log(data)                            if (data && (data.status === 1 || data.status === '1')) {                                location.reload();                            } else {                                alert(data.info)                            }                        },'json')                        setTimeout(function(){                            lockInfo = false;                        },1000);                    }                });                setTimeout(function(){                    lockInfo = false;                },1000);            }            function albumSwitch(reply_id, status) {                if (lockInfo) return;                lockInfo = true;                var url = '';                var close_topic = "{pigcms{:U('Community/close_reply')}";                var open_topic = "{pigcms{:U('Community/open_reply')}";                if (0 == status) {                    url = open_topic;                } else if(1 == status) {                    url = close_topic;                } else {                    return;                }                $.post(url,{'reply_id': reply_id},function(data){                    console.log(data)                    if (data && (data.status === 1 || data.status === '1')) {                        location.reload();                    } else {                        alert(data.info)                    }                },'json')                setTimeout(function(){                    lockInfo = false;                },1000);            }        </script><include file="Public:footer"/>