<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>                    <a href="{pigcms{:U('Community/dynamic_note',array('topic_id'=>$id['topic_id'],'topic_cate_id'=>$id['topic_cate_id']))}">帖子列表</a>|                    <a href=""  class="on">举报列表</a>                </ul>			</div>            <table class="search_table" width="100%">                <!-- <tr>                    <td style="width:60%;">                        <form action="{pigcms{:U('Community/dynamic_list')}" method="get">                            <input type="hidden" name="c" value="Community"/>                            <input type="hidden" name="a" value="dynamic_list"/>                            群名称:                               <input type="text" name="community_name" class="input-text" value=""/>                            <input type="submit" value="查询" class="button"/>                        </form>                    </td>                </tr> -->            </table>						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="100" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>                                <th>用户头像</th>                                <th>举报类别</th>                                <th>举报话题</th>                                <th>状态</th>                                <th>举报时间</th>                                <th>开启/禁止</th>                                <th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="$report_list">								<volist name="report_list" id="vo">									<tr>										<td>{pigcms{$vo.bind_id}</td>                                        <td><img src="$vo.avatar" width="150px;"></td>                                        <td>{pigcms{$vo.report_cate_name}</td>                                        <td>{pigcms{$vo.topic_title}</td>                                        <td>                                            <if condition="$vo['report_is_del'] eq 0">                                                开启                                                <else/>                                                禁止                                            </if>                                        </td>                                        <td>{pigcms{$vo.report_addtime|date='Y-m-d H:i:s',###}</td>                                        <td>                                            <span class="cb-enable"><label class="cb-enable <if condition="$vo['report_is_del'] eq 0">selected</if>"><span onclick="albumSwitch('{pigcms{$vo.bind_id}', 0)" >开启</span><input type="radio" value="0" <if condition="$vo['report_is_del'] eq 0">checked="checked"</if> /></label></span>                                            <span class="cb-disable"><label class="cb-disable <if condition="$vo['report_is_del'] eq 1">selected</if>"><span onclick="albumSwitch('{pigcms{$vo.bind_id}', 1)">禁止</span><input type="radio" value="1" <if condition="$vo['report_is_del'] eq 1">checked="checked"</if> /></label></span>                                        </td>                                        <td class="textcenter">                                            <a href="javascript:void(0);"  onclick='del_report("{pigcms{$vo.bind_id}")'>删除</a>                                        </td>                                    </tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$page}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>		<script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script>        <script>            var lockInfo = false;            function del_report(bind_id){                if (lockInfo) return;                lockInfo = true;                layer.open({                    title:['提示','background-color:#FF658E;color:#fff;'],                    content:'是否确认删除',                    shadeClose:false,                    offset: ['auto', '700px'],                    btn: ['确定', '取消'],                    yes:function(){                        var del_report = "{pigcms{:U('Community/del_report')}";                        $.post(del_report,{'bind_id':bind_id},function(data){                            console.log(data)                            if (data && (data.status === 1 || data.status === '1')) {                                location.reload();                            } else {                                alert(data.info)                            }                        },'json')                        setTimeout(function(){                            lockInfo = false;                        },1000);                    }                });                setTimeout(function(){                    lockInfo = false;                },1000);            }            function albumSwitch(bind_id, status) {                if (lockInfo) return;                lockInfo = true;                var url = '';                var close_report = "{pigcms{:U('Community/close_report')}";                var open_report = "{pigcms{:U('Community/open_report')}";                if (0 == status) {                    url = open_report;                } else if(1 == status) {                    url = close_report;                } else {                    return;                }                $.post(url,{'bind_id': bind_id},function(data){                    console.log(data)                    if (data && (data.status === 1 || data.status === '1')) {                        location.reload();                    } else {                        alert(data.info)                    }                },'json')                setTimeout(function(){                    lockInfo = false;                },1000);            }        </script><include file="Public:footer"/>