<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>                    <if condition="$topic_category">                        <a href="{pigcms{:U('Community/topic_cate')}"  class="on">话题分类列表</a>|                        <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Community/add_topic_cate')}','添加分类',700,480,true,false,false,addbtn,'add',true);">添加分类</a>                    </if>                </ul>			</div>						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="100" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>                                <th>类型名称</th>                                <th>状态</th>                                <th>包含话题</th>                                <th>添加时间</th>                                <th>开启/禁止</th>                                <th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="$topic_category">								<volist name="topic_category" id="vo">									<tr>										<td>{pigcms{$vo.topic_cate_id}</td>                                        <td>{pigcms{$vo.topic_cate_name}</td>                                        <td>                                            <if condition="$vo['topic_cate_is_del'] eq 0">                                                开启                                                <else/>                                                禁止                                            </if>                                        </td>                                        <td><a href="{pigcms{:U('Community/topic_list',array('topic_cate_id'=>$vo[topic_cate_id]))}">查看</a></td>                                        <td>{pigcms{$vo.topic_cate_addtime|date='Y-m-d H:i:s',###}</td>                                        <td>                                            <span class="cb-enable"><label class="cb-enable <if condition="$vo['topic_cate_is_del'] eq 0">selected</if>"><span onclick="discoverSwitch('{pigcms{$vo.topic_cate_id}', 0)" >开启</span><input type="radio" value="0" <if condition="$vo['topic_cate_is_del'] eq 0">checked="checked"</if> /></label></span>                                            <span class="cb-disable"><label class="cb-disable <if condition="$vo['topic_cate_is_del'] eq 1">selected</if>"><span onclick="discoverSwitch('{pigcms{$vo.topic_cate_id}', 1)">禁止</span><input type="radio" value="1" <if condition="$vo['topic_cate_is_del'] eq 1">checked="checked"</if> /></label></span>                                        </td>                                        <td class="textcenter">                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Community/edit_topic_cate',array('topic_cate_id'=>$vo['topic_cate_id']))}','编辑分类信息',700,480,true,false,false,editbtn,'add',true);" >编辑</a>                                            | <a href="javascript:void(0);"  onclick='del_category("{pigcms{$vo.topic_cate_id}")'>删除</a>                                        </td>                                    </tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$page}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>		<script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script>        <script>            var lockInfo = false;            function del_category(topic_cate_id){                if (lockInfo) return;                lockInfo = true;                var del_category = "{pigcms{:U('Community/del_topic_cate')}";                $.post(del_category,{'topic_cate_id':topic_cate_id},function(data){                    console.log(data)                    if (data && (data.status === 1 || data.status === '1')) {                        location.reload();                    } else {                        alert(data.info)                    }                },'json')                setTimeout(function(){                    lockInfo = false;                },1000);            }            function discoverSwitch(topic_cate_id, status) {                if (lockInfo) return;                lockInfo = true;                var url = '';                var close_category = "{pigcms{:U('Community/close_topic_cate')}";                var open_category = "{pigcms{:U('Community/open_topic_cate')}";                if (0 == status) {                    url = open_category;                } else if(1 == status) {                    url = close_category;                } else {                    return;                }                $.post(url,{'topic_cate_id': topic_cate_id},function(data){                    console.log(data)                    if (data && (data.status === 1 || data.status === '1')) {                        location.reload();                    } else {                        alert(data.info)                    }                },'json')                setTimeout(function(){                    lockInfo = false;                },1000);            }        </script><include file="Public:footer"/>