<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>                    <a href="{pigcms{:U('Community/topic_cate')}">话题分类列表</a>|                    <a href=""  class="on">话题列表</a>                </ul>			</div>            <!-- <table class="search_table" width="100%">                <tr>                    <td style="width:60%;">                        <form action="{pigcms{:U('Community/album_list')}" method="get">                            <input type="hidden" name="c" value="Community"/>                            <input type="hidden" name="a" value="album_list"/>                            <input type="hidden" name="community_id" value="{pigcms{$_GET['community_id']}"/>                            <input type="text" name="album_name" class="input-text" value="{pigcms{$_GET['album_name']}"/>                            <input type="submit" value="查询" class="button"/>                        </form>                    </td>                </tr>            </table> -->						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="100" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>                                <th>创建者</th>                                <th>标题</th>                                <th>内容</th>                                <th>包含帖子-动态</th>                                <th>话题图片</th>                                <th>状态</th>                                <th>添加时间</th>                                <th>开启/禁止</th>                                <th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="$topic_list">								<volist name="topic_list" id="vo">									<tr>										<td>{pigcms{$vo.topic_id}</td>                                        <td>{pigcms{$vo.nickname}</td>                                        <td>{pigcms{$vo.topic_title}</td>                                        <td>{pigcms{$vo.topic_content}</td>                                        <td><a href="{pigcms{:U('Community/dynamic_note',array('topic_id'=>$vo[topic_id],'topic_cate_id'=>$vo['topic_cate_id']))}">查看</a></td>                                        <td><a href="{pigcms{:U('Community/topic_img',array('topic_id'=>$vo['topic_id']))}">查看</a></td>                                        <td>                                            <if condition="$vo['topic_is_del'] eq 0">                                                开启                                                <else/>                                                禁止                                            </if>                                        </td>                                        <td>{pigcms{$vo.topic_addtime|date='Y-m-d H:i:s',###}</td>                                        <td>                                            <span class="cb-enable"><label class="cb-enable <if condition="$vo['topic_is_del'] eq 0">selected</if>"><span onclick="albumSwitch('{pigcms{$vo.topic_id}', 0)" >开启</span><input type="radio" value="0" <if condition="$vo['topic_is_del'] eq 0">checked="checked"</if> /></label></span>                                            <span class="cb-disable"><label class="cb-disable <if condition="$vo['topic_is_del'] eq 1">selected</if>"><span onclick="albumSwitch('{pigcms{$vo.topic_id}', 1)">禁止</span><input type="radio" value="1" <if condition="$vo['topic_is_del'] eq 1">checked="checked"</if> /></label></span>                                        </td>                                        <td class="textcenter">                                            <a href="javascript:void(0);"  onclick='del_topic("{pigcms{$vo.topic_id}")'>删除</a>                                        </td>                                    </tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$page}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>		<script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script>        <script>            var lockInfo = false;            function del_topic(topic_id){                if (lockInfo) return;                lockInfo = true;                var del_topic = "{pigcms{:U('Community/del_topic')}";                $.post(del_topic,{'topic_id':topic_id},function(data){                    console.log(data)                    if (data && (data.status === 1 || data.status === '1')) {                        location.reload();                    } else {                        alert(data.info)                    }                },'json')                setTimeout(function(){                    lockInfo = false;                },1000);            }            function albumSwitch(topic_id, status) {                if (lockInfo) return;                lockInfo = true;                var url = '';                var close_topic = "{pigcms{:U('Community/close_topic')}";                var open_topic = "{pigcms{:U('Community/open_topic')}";                if (0 == status) {                    url = open_topic;                } else if(1 == status) {                    url = close_topic;                } else {                    return;                }                $.post(url,{'topic_id': topic_id},function(data){                    console.log(data)                    if (data && (data.status === 1 || data.status === '1')) {                        location.reload();                    } else {                        alert(data.info)                    }                },'json')                setTimeout(function(){                    lockInfo = false;                },1000);            }        </script><include file="Public:footer"/>