<include file="Public:header"/><form id="myform" method="post" action="{pigcms{:U('company_save_data')}">	<table cellpadding="0" cellspacing="0" class="frame_form" width="100%">		<input type="hidden" name="company_id" value="{pigcms{$companyInfo.company_id}">		<tr>			<td width="80">公司名称</td>			<td>				<input type="text" class="input fl" name="name" value="{pigcms{$companyInfo.name}" placeholder="请填写公司名称" validate="required:true"></td>		</tr>		<tr>			<td width="80">帐号</td>			<td>				<input type="text" class="input fl" name="account" value="{pigcms{$companyInfo.account}" placeholder="请填写帐号" validate="required:true"></td>		</tr>		<tr>			<td width="80">密码</td>			<td>				<input type="password" class="input fl" name="pwd" value="" placeholder="请填写密码"></td>		</tr>		<tr>			<td width="80">手机号</td>			<td>				<input type="text" class="input fl" name="phone" value="{pigcms{$companyInfo.phone}" placeholder="请填写手机号" validate="required:true"></td>		</tr>		<tr>			<td width="80">状态</td>			<td class="radio_box">				<input type="radio" name="status" value="2" <if condition="$companyInfo['status'] eq 2">checked="checked"</if>>&nbsp;<span style=" color: green;">正常</span>				&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;				<input type="radio" name="status" value="1" <if condition="$companyInfo['status'] eq 1">checked="checked"</if>>&nbsp;<span style=" color: red;">关闭</span>				&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;				<input type="radio" name="status" value="0" <if condition="$companyInfo['status'] eq 0">checked="checked"</if>>&nbsp;<span style=" color: red;">待审核</span>			</td>		</tr>	</table>	<div class="btn hidden">		<input type="submit" name="dosubmit" id="dosubmit" value="提交" class="button" />		<input type="reset" value="取消" class="button" />	</div></form><include file="Public:footer"/>