<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Company/index')}" class="on">公司列表</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>公司名称</th>								<th>帐号</th>								<th>手机号</th>								<th>添加时间</th>								<th>状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($companyList)">								<volist name="companyList" id="vo">									<tr>										<td>{pigcms{$vo.company_id}</td>										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$vo.account}</td>										<td>{pigcms{$vo.phone}</td>										<td>{pigcms{$vo.add_time|date='Y-m-d H:i:s',###}</td>										<td>											<if condition="$vo['status'] eq 2"><font color="green">正常</font><elseif condition="$vo['status'] eq 1"/><font color="red">关闭</font><elseif condition="$vo['status'] eq 0"/><font color="red">待审核</font></if>										</td>										<td class="textcenter">											<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('company_save',array('company_id'=>$vo['company_id']))}','编辑公司信息',600,300,true,false,false,editbtn,'edit',true);">编辑</a>|											<a href="javascript:void(0);" class="delete_row" parameter="company_id={pigcms{$vo.company_id}" url="{pigcms{:U('company_del')}">删除</a>									  	</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="7">{pigcms{$pagebar}</td></tr>							<else/>															<tr><td class="textcenter red" colspan="7">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>