<include file="Public:header"/><form id="myform" method="post" action="" enctype="multipart/form-data">    <input type="hidden" name="type_id" value="{pigcms{$info['type_id']}"/>    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <th width="80">分类名称</th>            <td><input type="text" class="input fl" name="type_name" id="type_name" value="{pigcms{$info.type_name}" size="25" placeholder="" validate="maxlength:20,required:true" tips=""/></td>        </tr>    </table>    <div class="btn hidden">        <input type="submit" name="dosubmit" id="dosubmit" value="提交" class="button" />        <input type="reset" value="取消" class="button" />    </div></form><include file="Public:footer"/>