<include file="Public:header"/><form id="myform" method="post" action="" enctype="multipart/form-data">    <input type="hidden" name="type_id" value="{pigcms{$info['type_id']}"/>    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <th width="80">分类id</th>            <td>                {pigcms{$info.type_id}            </td>        </tr>        <tr>            <th width="80">分类名称</th>            <td><input type="text" class="input fl" name="type_name" id="type_name" value="{pigcms{$info.type_name}" size="25" placeholder="" validate="maxlength:20,required:true" tips=""/></td>        </tr>        <tr>            <th width="80">状态</th>            <td>                <span class="cb-enable"><label class="cb-enable <if condition="$info['type_status'] eq 1">selected</if>"><span>开启</span><input name="type_status" type="radio" value="1" <if condition="$info['type_status'] eq 1">checked="checked"</if> /></label></span>                <span class="cb-disable"><label class="cb-disable <if condition="$info['type_status'] eq 2">selected</if>"><span>禁止</span><input name="type_status" type="radio" value="2" <if condition="$info['type_status'] eq 2">checked="checked"</if> /></label></span>            </td>        </tr>        <tr>            <th width="80">添加时间</th>            <td>                {pigcms{$info.type_add_time|date='Y-m-d H:i:s',###}            </td>        </tr>    </table>    <div class="btn hidden">        <input type="submit" name="dosubmit" id="dosubmit" value="提交" class="button" />        <input type="reset" value="取消" class="button" />    </div></form><include file="Public:footer"/>