<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Discover/index')}"  class="on">信息列表</a>				</ul>			</div>						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>                            <col/>                            <col/>                            <col/>                            <col/>                            <col/>							<col width="100" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>                                <th>分类</th>                                <th>内容</th>                                <th>发布者</th>                                <th>状态</th>                                <th>收藏人数</th>                                <th>添加时间</th>                                <th>关闭/开启</th>                                <th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="$discover_msg">								<volist name="discover_msg" id="vo">									<tr>                                        <td>{pigcms{$vo.discover_id}</td>                                        <td>{pigcms{$vo.type_info.type_name}</td>                                        <td>{pigcms{$vo['discover_content']|msubstr=###,0,50}</td>                                        <td>{pigcms{$vo.user.nickname}</td>                                        <td>                                            <if condition="$vo['discover_status'] eq 1">                                                开启                                                <elseif condition="$vo['discover_status'] eq 2"/>                                                关闭                                                <else/>                                                删除                                            </if>                                        </td>                                        <td>{pigcms{$vo.collection_num}</td>                                        <td>{pigcms{$vo.add_time|date='Y-m-d H:i:s',###}</td>                                        <td>                                            <span class="cb-enable"><label class="cb-enable <if condition="$vo['discover_status'] eq 1">selected</if>"><span onclick="discoverSwitch('{pigcms{$vo.discover_id}', 1)" >开启</span><input type="radio" value="1" <if condition="$vo['discover_status'] eq 1">checked="checked"</if> /></label></span>                                            <span class="cb-disable"><label class="cb-disable <if condition="$vo['discover_status'] eq 2">selected</if>"><span onclick="discoverSwitch('{pigcms{$vo.discover_id}', 2)">关闭</span><input type="radio" value="2" <if condition="$vo['discover_status'] eq 2">checked="checked"</if> /></label></span>                                        </td>                                        <td class="textcenter">                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Discover/look_discover_msg',array('discover_id'=>$vo['discover_id']))}','查看信息',680,500,true,false,false,false,'detail',true);" >查看</a>                                            <if condition="$vo['status'] neq 3">                                                |                                                <a href="javascript:void(0);"  onclick='del_discover_msg("{pigcms{$vo.discover_id}")'>删除</a>                                            </if>                                        </td>                                    </tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$page}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>		<script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script>        <script>            var lockInfo = false;            function del_discover_msg(discover_id){                if (lockInfo) return;                lockInfo = true;                var del_discover_msg = "{pigcms{:U('Discover/del_discover_msg')}";                $.post(del_discover_msg,{'discover_id':discover_id},function(data){                    console.log(data)                    if (data && (data.status === 1 || data.status === '1')) {                        location.reload();                    } else {                        alert(data.info)                    }                },'json')                console.log('数据信息', discover_id)                setTimeout(function(){                    lockInfo = false;                },1000);            }            function discoverSwitch(discover_id, status) {                if (lockInfo) return;                lockInfo = true;                var url = '';                var close_discover_msg = "{pigcms{:U('Discover/close_discover_msg')}";                var open_discover_msg = "{pigcms{:U('Discover/open_discover_msg')}";                if (1 == status) {                    url = open_discover_msg;                } else if(2 == status) {                    url = close_discover_msg;                } else {                    return;                }                $.post(url,{'discover_id': discover_id},function(data){                    console.log(data)                    if (data && (data.status === 1 || data.status === '1')) {                        location.reload();                    } else {                        alert(data.info)                    }                },'json')                console.log('数据信息', discover_id, status)                setTimeout(function(){                    lockInfo = false;                },1000);            }        </script><include file="Public:footer"/>