<include file="Public:header"/><form id="myform" method="post" action="" enctype="multipart/form-data">    <input type="hidden" name="cid" value="{pigcms{$info['discover_id']}"/>    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <th width="80">信息id</th>            <td>                {pigcms{$info.discover_id}            </td>        </tr>        <tr>            <th width="80">发布者</th>            <td>                {pigcms{$info.nickname}            </td>        </tr>        <tr>            <th width="80">所属类型</th>            <td>                {pigcms{$info.type_name}            </td>        </tr>        <tr>            <th width="80">收藏人数</th>            <td>                {pigcms{$info.collection_num}            </td>        </tr>        <tr>            <th width="80">内容</th>            <td>                {pigcms{$info.discover_content}            </td>        </tr>        <tr>            <th width="80">添加时间</th>            <td>                {pigcms{$info.add_time|date='Y-m-d H:i:s',###}            </td>        </tr>    </table>    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <td width="100">上传的图片：</td>        </tr>        <volist name="img_list" id="vv">            <php>if($mod==0)echo "<tr>";</php>            <td><img class="view_msg" style="width:300px;" src="{pigcms{$vv}"></td>            <php>if($mod==1)echo "</tr>";</php>        </volist>    </table></form><include file="Public:footer"/>