<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Foodshop/order')}" class="on">订单列表</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Foodshop/order')}" method="get">							<input type="hidden" name="c" value="Foodshop"/>							<input type="hidden" name="a" value="order"/>							搜索: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="real_orderid" <if condition="$_GET['searchtype'] eq 'real_orderid'">selected="selected"</if>>订单流水号</option>								<option value="third_id" <if condition="$_GET['searchtype'] eq 'third_id'">selected="selected"</if>>第三方支付流水号</option>								<option value="s_name" <if condition="$_GET['searchtype'] eq 's_name'">selected="selected"</if>>店铺名称</option>								<option value="name" <if condition="$_GET['searchtype'] eq 'name'">selected="selected"</if>>客户名称</option>								<option value="phone" <if condition="$_GET['searchtype'] eq 'phone'">selected="selected"</if>>客户电话</option>							</select>							<font color="#000">日期筛选：</font>							<input type="text" class="input-text" name="begin_time" style="width:120px;" id="d4311"  value="{pigcms{$_GET.begin_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>			   							<input type="text" class="input-text" name="end_time" style="width:120px;" id="d4311" value="{pigcms{$_GET.end_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>							订单状态筛选: 							<select id="status" name="status">								<volist name="status_list" id="vo">									<option value="{pigcms{$key}" <if condition="$key eq $status">selected="selected"</if>>{pigcms{$vo}</option>								</volist>							</select>							支付方式筛选: 							<select id="pay_type" name="pay_type">									<option value="" <if condition="'' eq $pay_type">selected="selected"</if>>全部支付方式</option>								<volist name="pay_method" id="vo">									<option value="{pigcms{$key}" <if condition="$key eq $pay_type">selected="selected"</if>>{pigcms{$vo.name}</option>								</volist>									<option value="balance" <if condition="'balance' eq $pay_type">selected="selected"</if>>余额支付</option>							</select>														<input type="submit" value="查询" class="button"/>　　						</form>					</td>					<td>					<a  href="javascript:void(0)" onclick="exports()" class="button" style="float:right;margin-right: 10px;">导出订单</a>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>订单流水号</th>								<th>商家名称</th>								<th>店铺名称</th>								<th>客户姓名</th>								<th>客户电话</th>																<th width="80" class="button-column">预订金</th>								<th width="80">预订时间								<if condition="$type eq 'book_time' AND false">									<if condition="$sort eq 'ASC'">										<a href="{pigcms{:U('Foodshop/order', array('store_id' => $now_store['store_id'], 'type' => 'book_time', 'sort' => 'DESC', 'status' => $status))}">预订时间<i class="menu-icon fa fa-sort-desc"></i></a>									<elseif condition="$sort eq 'DESC'" />										<a href="{pigcms{:U('Foodshop/order', array('store_id' => $now_store['store_id'], 'type' => 'book_time', 'sort' => 'ASC', 'status' => $status))}">预订时间<i class="menu-icon fa fa-sort-asc"></i></a>									<else />										<a href="{pigcms{:U('Foodshop/order', array('store_id' => $now_store['store_id'], 'type' => 'book_time', 'sort' => 'DESC', 'status' => $status))}">预订时间<i class="menu-icon fa fa-sort"></i></a>									</if>								<elseif condition="false" />									<a href="{pigcms{:U('Foodshop/order', array('store_id' => $now_store['store_id'], 'type' => 'book_time', 'sort' => 'DESC', 'status' => $status))}">预订时间<i class="menu-icon fa fa-sort"></i></a>								</if>								</th>																<th width="80" class="button-column">桌台类型</th>								<th width="80" class="button-column">桌台名称</th>								<th width="80" class="button-column">订单状态</th>								<th width="80" class="button-column">订单总价								<if condition="$type eq 'price' AND false">									<if condition="$sort eq 'ASC'">										<a href="{pigcms{:U('Foodshop/order', array('store_id' => $now_store['store_id'], 'type' => 'price', 'sort' => 'DESC', 'status' => $status))}">订单总价<i class="menu-icon fa fa-sort-desc"></i></a>									<elseif condition="$sort eq 'DESC'" />										<a href="{pigcms{:U('Foodshop/order', array('store_id' => $now_store['store_id'], 'type' => 'price', 'sort' => 'ASC', 'status' => $status))}">订单总价<i class="menu-icon fa fa-sort-asc"></i></a>									<else />										<a href="{pigcms{:U('Foodshop/order', array('store_id' => $now_store['store_id'], 'type' => 'price', 'sort' => 'DESC', 'status' => $status))}">订单总价<i class="menu-icon fa fa-sort"></i></a>									</if>								<elseif condition="false"  />									<a href="{pigcms{:U('Foodshop/order', array('store_id' => $now_store['store_id'], 'type' => 'price', 'sort' => 'DESC', 'status' => $status))}">订单总价<i class="menu-icon fa fa-sort"></i></a>								</if>								</th>								<th width="80" class="button-column">查看订单详情</th>							</tr>						</thead>						<tbody>							<if condition="is_array($order_list)">								<volist name="order_list" id="vo">									<tr>										<td>{pigcms{$vo.real_orderid}</td>										<td>{pigcms{$vo.merchant_name}</td>										<td>{pigcms{$vo.store_name}</td>										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$vo.phone}</td>										<td>{pigcms{$vo.book_price|floatval}</td>										<if condition="$vo['book_time']">											<td>{pigcms{$vo.book_time|date='Y-m-d H:i:s',###}</td>										<else />											<td>--</td>										</if>										<td class="button-column">{pigcms{$vo.table_type_name}</td>										<td class="button-column">{pigcms{$vo.table_name}</td>										<td class="button-column">{pigcms{$vo.show_status}</td>										<if condition="$vo['status'] gt 2">										<td class="button-column" style="color:green;">{pigcms{$vo.price|floatval}</td>										<else />										<td class="button-column" style="color:red;">还未买单</td>										</if>										<td class="textcenter">											<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Foodshop/order_detail',array('order_id'=>$vo['order_id'],'frame_show'=>true))}','查看{pigcms{$config.meal_alias_name}订单详情',480,380,true,false,false,false,'detail',true);">查看</a>									  	</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="19">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="19">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><script>$(function(){	// $('#status').change(function(){		// location.href = "{pigcms{:U('Foodshop/order', array('type' => $type, 'sort' => $sort))}&status=" + $(this).val();	// });		// $('#pay_type').change(function(){		// location.href = "{pigcms{:U('Foodshop/order', array('type' => $type, 'sort' => $sort))}&pay_type=" + $(this).val();	// });	});	   var url = "{pigcms{$config.site_url}"    var export_url = "{pigcms{:U('Foodshop/export')}"</script><script type="text/javascript" src="{pigcms{$static_public}js/export.js"> </script><include file="Public:footer"/>