<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Foodshop/shop_list')}" class="on">{pigcms{$config['meal_alias_name']}店铺列表</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Foodshop/shop_list')}" method="get">							<input type="hidden" name="c" value="Foodshop"/>							<input type="hidden" name="a" value="shop_list"/>							店铺名: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<input type="submit" value="查询" class="button"/>						</form>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>商家名称</th>								<th>店铺名称</th>								<if condition="!isset($config['not_o2o_demo'])">								<th>商家电话</th>								<th>店铺电话</th>								</if>								<th>创建时间</th>								<th>店铺排序</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($order_list)">								<volist name="order_list" id="vo">									<tr>										<td>{pigcms{$vo.merchant_name}</td>										<td>{pigcms{$vo.store_name}</td>										<if condition="!isset($config['not_o2o_demo'])">										<td>{pigcms{$vo.merchant_phone}</td>										<td>{pigcms{$vo.store_phone}</td>										</if>										<td>{pigcms{$vo.create_time|date="Y-m-d H:i:s",###}</td>										<td>{pigcms{$vo.sort}</td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Foodshop/edit_sort',array('store_id' => $vo['store_id']))}','修改店铺排序',380,280,true,false,false,editbtn,'edit',true);">设置店铺排序</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="13">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="13">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>