<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>										<a href="{pigcms{:U('Goods/properties_value',array('pid'=>$now_properties['id']))}" class="on">{pigcms{$now_properties.name} - 属性值列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Goods/value_add', array('pid' => $now_properties['id']))}','添加属性值',480,260,true,false,false,addbtn,'add',true);">添加属性值</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>属性值</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($value_list)">								<volist name="value_list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.name}</td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Goods/value_edit',array('id'=>$vo['id'], 'id'=>$vo['id']))}','编辑属性值',480,260,true,false,false,editbtn,'edit',true);">编辑</a>  | <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('Goods/value_del')}">删除</a></td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="3">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>