<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>										<a href="{pigcms{:U('Goods/index',array('parentid'=>$category['fid']))}" class="on">{pigcms{$category.name} - 商品分类的属性列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Goods/property_add', array('cat_id' => $category['id']))}','添加商品分类属性',480,260,true,false,false,addbtn,'add',true);">添加商品分类属性</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>名称</th>								<th>查看属性值</th>								<th>状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($properties_list)">								<volist name="properties_list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.name}</td>										<td>										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('properties_value',array('pid'=>$vo['id']))}','【{pigcms{$vo.name}】对应的值列表',580,420,true,false,false,false,'detail',true);">查看属性值</a>										</td>										<td><if condition="$vo['status'] eq 1"><font color="green">启用</font><else/><font color="red">关闭</font></if></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Goods/property_edit',array('id'=>$vo['id'],'frame_show'=>true))}','查看分类信息',480,260,true,false,false,false,'detail',true);">查看</a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Goods/property_edit',array('cat_id'=>$vo['cat_id'], 'id'=>$vo['id']))}','编辑分类信息',480,260,true,false,false,editbtn,'edit',true);">编辑</a><!--  | <a href="javascript:void(0);" class="delete_row" parameter="cat_id={pigcms{$vo.id}" url="{pigcms{:U('Goods/cat_del')}">删除</a> --></td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="5">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>