<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>										<a href="{pigcms{:U('Goods/wholesale_category')}" <if condition="!$category">class="on"</if>>批发市场分类列表</a>|					<if condition="$category">					<a href="{pigcms{:U('Goods/wholesale_category',array('parentid'=>$category['id']))}" class="on">{pigcms{$category.name} - 批发市场子分类列表</a>|					</if>					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Goods/wholesale_cat_add', array('parentid' => $parentid))}','添加批发市场分类',480,260,true,false,false,addbtn,'add',true);">添加批发市场<if condition="$category">子<else />主</if>分类</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>名称</th>								<if condition="empty($parentid)">									<th>查看子分类</th>								<else/>									<!-- <th>查看分类属性</th> -->								</if>								<th>状态</th>								<!-- <th>是否热门</th> -->								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($category_list)">								<volist name="category_list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.name}</td>										<if condition="empty($parentid)">											<td><a href="{pigcms{:U('Goods/wholesale_category',array('parentid'=>$vo['id']))}">查看子分类</a></td>										<else/>											<!-- <td><a href="{pigcms{:U('Goods/property',array('cat_id'=>$vo['id']))}">查看批发市场分类的属性</a></td> -->																					</if>										<td><if condition="$vo['status'] eq 1"><font color="green">启用</font><else/><font color="red">关闭</font></if></td>										<!-- <td><if condition="$vo['is_hot'] eq 1"><font color="green">是</font><else/>否</if></td> -->										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Goods/wholesale_cat_edit',array('cat_id'=>$vo['id'],'frame_show'=>true))}','查看分类信息',480,260,true,false,false,false,'detail',true);">查看</a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Goods/wholesale_cat_edit',array('cat_id'=>$vo['id'], 'parentid'=>$vo['fid']))}','编辑分类信息',480,260,true,false,false,editbtn,'edit',true);">编辑</a><!--  | <a href="javascript:void(0);" class="delete_row" parameter="cat_id={pigcms{$vo.id}" url="{pigcms{:U('Goods/cat_del')}">删除</a> --></td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="6">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>