<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<if condition="!$now_groupwxapp_category">						<if condition="empty($_GET['mer_id'])">							<a href="{pigcms{:U('Group/product')}" class="on">商品列表</a>						<else/>							<a href="{pigcms{:U('Group/product')}">商品列表</a> |							<a href="{pigcms{:U('Group/product',array('mer_id'=>$_GET['mer_id']))}" class="on">指定商家的商品列表</a>						</if>					<else/>						<div id="nav" class="mainnav_title">							<ul>								<a href="{pigcms{:U('Groupwxapp/index')}">本地优选分类列表</a>|								<a href="{pigcms{:U('Group/product',$_GET)}" class="on">商品列表 - {pigcms{$now_groupwxapp_category.cat_name}</a>|								<a href="{pigcms{:U('Config/index',array('galias'=>'group_wxapp','header'=>'Groupwxapp/header'))}">本地优选配置</a>							</ul>						</div>					</if>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Group/product')}" method="get">							<input type="hidden" name="c" value="Group"/>							<input type="hidden" name="a" value="product"/>							<input type="hidden" name="mer_id" value="{pigcms{$_GET.mer_id}"/>							筛选: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="group_id" <if condition="$_GET['searchtype'] eq 'group_id'">selected="selected"</if>>商品编号</option>								<option value="s_name" <if condition="$_GET['searchtype'] eq 's_name'">selected="selected"</if>>商品名称</option>								<option value="name" <if condition="$_GET['searchtype'] eq 'name'">selected="selected"</if>>商品标题</option>							</select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;							团购状态: <select name="searchstatus">								<option value="0" <if condition="$_GET['searchstatus'] eq 0">selected="selected"</if>>全部</option>								<option value="1" <if condition="$_GET['searchstatus'] eq '1'">selected="selected"</if>>进行状态</option>								<option value="2" <if condition="$_GET['searchstatus'] eq '2'">selected="selected"</if>>关闭状态（或已结束）</option>							</select>							<input type="submit" value="查询" class="button"/>														<if condition="$now_groupwxapp_category">								<input type="button" value="添加优选商品" class="button" onclick="window.top.artiframe('{pigcms{:U('Groupwxapp/product_add',$_GET)}','添加优选商品 - {pigcms{$now_groupwxapp_category.cat_name}',600,400,true,false,false,addbtn,'edit',true);" style="float:right;margin-right:100px;"/>							</if>						</form>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="250" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>名称（悬浮查看商品标题）</th>								<th>价格</th>								<th>销售概览</th>								<th>时间</th>								<th>数字</th>								<th>查看二维码</th>								<th>运行状态</th>								<th>{pigcms{$config.group_alias_name}状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($group_list)">								<volist name="group_list" id="vo">									<tr>										<td>{pigcms{$vo.group_id}</td>										<td><a href="{pigcms{$config.site_url}/group/{pigcms{$vo.group_id}.html" target="_blank" title="{pigcms{$vo.name}">{pigcms{$vo.s_name}</a></td>										<td>{pigcms{$config.group_alias_name}价：￥{pigcms{$vo.price}元<br/>原价：￥{pigcms{$vo.old_price}元										<if condition="$now_groupwxapp_category['cat_id']"><br/><span style="color:red;">优选价：￥{pigcms{$vo.product_price}元</span>										</if>										</td>										<td>售出：{pigcms{$vo.sale_count} 份<br/><!--库存： <if condition="$vo['count_num']">{pigcms{$vo.count_num}<else/>无限制</if><br/>虚拟：{pigcms{$vo.virtual_num} 人--></td>										<td>开始时间：{pigcms{$vo.begin_time|date='Y-m-d H:i:s',###}<br/>结束时间：{pigcms{$vo.end_time|date='Y-m-d H:i:s',###}<br/>有效期：{pigcms{$vo.deadline_time|date='Y-m-d H:i:s',###}</td>										<td>查看数：{pigcms{$vo.hits}<br/>出售数：{pigcms{$vo.sale_count}<br/>评论数：{pigcms{$vo.reply_count}</td>										<td><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{$config.site_url}/index.php?g=Index&c=Recognition&a=see_qrcode&type=group&id={pigcms{$vo.group_id}&img=1','查看二维码',430,433,true,false,false,null,'merchant_qrcode',true);" class="see_qrcode">查看二维码</a></td>										<td>											<if condition="$vo['begin_time'] gt $_SERVER['REQUEST_TIME']">												未开团											<elseif condition="$vo['end_time'] lt $_SERVER['REQUEST_TIME']"/>												已结束											<elseif condition="$vo['type'] eq 3"/>												团购成功结束											<else/>												进行中											</if>										</td>										<td><switch name="vo['status']"><case value="0"><font color="red">关闭</font></case><case value="1"><font color="green">正常</font></case><case value="2"><font color="red">审核中</font></case></switch></td>										<td class="textcenter">										<a href="{pigcms{:U('Group/order_list',array('group_id'=>$vo['group_id']))}">订单列表</a> | <a href="{pigcms{:U('Group/reply_list',array('group_id'=>$vo['group_id']))}">评论列表</a> | <a href="{pigcms{:U('Merchant/merchant_login',array('mer_id'=>$vo['mer_id'],'group_id'=>$vo['group_id']))}">编辑</a>										<if condition="$now_groupwxapp_category['cat_id']"><br/><a href="javascript:void(0);"onclick="window.top.artiframe('{pigcms{:U('Groupwxapp/product_edit',array('group_id'=>$vo['group_id']))}','编辑优选商品',600,460,true,false,false,editbtn,'edit',true);">编辑优选</a> | <a href="javascript:void(0);" class="delete_row" parameter="group_id={pigcms{$vo.group_id}" url="{pigcms{:U('Groupwxapp/product_del')}" tip="">删除优选</a></if>										</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>