<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Group/spread')}" class="on">佣金获得记录</a>				</ul>			</div>						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="100" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>获得佣金的用户</th>								<th>购买的用户</th>								<th>订单号</th>								<th>获得详情</th>								<th>金额</th>								<th>佣金状态</th>								<th>时间</th>							</tr>						</thead>						<tbody>							<if condition="is_array($list)">								<volist name="list" id="vo">									<tr>										<td>{pigcms{$vo.pigcms_id}</td>										<td>{pigcms{$vo.get_nickname}</td>										<td>{pigcms{$vo.buy_nickname}</td>										<td>{pigcms{$vo.order_id}</td>										<td>{pigcms{$vo.txt}</td>										<td>{pigcms{$vo.money}</td>										<td>											<if condition="$vo['status'] eq 0">												<font color="red">未结算</font>											<elseif condition="$vo['status'] eq 1" />												<font color="green">已结算</font>											<elseif condition="$vo['status'] eq 2" />												<font color="red">订单退款不予结算</font>											</if>										</td>										<td>{pigcms{$vo['add_time']|date='Y-m-d H:i:s',###}</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>