<include file="Public:header"/><style>	img{		width:100px;		height:50px;	}</style>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('House/open_door_adver')}" >开门广告列表</a>|					<a href="{pigcms{:U('House/open_door_adv_village')}&adver_id={pigcms{$_GET.adver_id}" class="on">开门广告小区覆盖</a>									</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="" method="get">							<input type="hidden" name="c" value="House"/>							<input type="hidden" name="a" value="open_door_adv_village"/>							筛选: 							<if condition="$admin_area neq 3">								<div id="choose_pca" province_idss="{pigcms{$_GET.province_idss}" city_idss="{pigcms{$_GET.city_idss}" area_id="{pigcms{$_GET.area_id}" style="display:inline"></div>							</if>							<input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="village_name" <if condition="$_GET['searchtype'] eq 'village_name'">selected="selected"</if>>小区名称</option>								<option value="property_name" <if condition="$_GET['searchtype'] eq 'property_name'">selected="selected"</if>>物业名称</option>								<option value="property_phone" <if condition="$_GET['searchtype'] eq 'property_phone'">selected="selected"</if>>物业电话</option>								<option value="account" <if condition="$_GET['searchtype'] eq 'account'">selected="selected"</if>>社区账号</option>								<option value="village_id" <if condition="$_GET['searchtype'] eq 'village_id'">selected="selected"</if>>社区ID</option>							</select>							<input type="submit" value="查询" class="button"/>							<input type="button" id="allup" value="批量上线" class="button"/>							<input type="button" id="alldown" value="批量下线" class="button"/>						</form>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup><col> <col> <col><col width="180" align="center"> </colgroup>						<thead>							<tr>								<th>全选<input type="checkbox" name="select_all" id="select_all"> 编号</th>								<th>小区名称</th>								<th>状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($village_list)">								<volist name="village_list" id="vo">									<tr>										<td><input type="checkbox" name="village_id[]" value="{pigcms{$vo.village_id}" class="single_select" >{pigcms{$vo.village_id}</td> 										<td>{pigcms{$vo.village_name}</td>										<td><if condition="$vo['status'] eq 1"><font color="green">正常</font><elseif condition="$vo['status'] eq 0"/><font color="red" title="等待小区管理员登录社区后台完善信息">待完善信息</font><else/><font color="red">禁止</font></if></td>										<td class="textcenter" data-village_id="{pigcms{$vo.village_id}"  >																					<span class="cb-enable">												<label class="cb-enable <php>if($adver_list[$vo['village_id']]['status']==1){</php>selected<php>}</php>"><span>已上线</span>												<input type="radio" name="show_adver" value="1" <php>if($adver_list[$vo['village_id']]['status']==1){</php>checked="checked"<php>}</php>/>												</label>											</span>											<span class="cb-disable">												<label class="cb-disable <php>if($adver_list[$vo['village_id']]['status']==0){</php>selected<php>}</php>"><span>未上线</span>												<input type="radio" name="show_adver" value="0" <php>if($adver_list[$vo['village_id']]['status']==0){</php>checked="checked"<php>}</php>/>												</label>											</span>										</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="6">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="10">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>		<script>					$(function(){				$('input[name="show_adver"]:radio').click(function(){					var status = $(this).val();					var village_id = $(this).parents('.textcenter').data('village_id')					$.post('{pigcms{:U("ajax_change_adver_village")}',{village_id:village_id,status:status,adver_id:'{pigcms{$_GET.adver_id}'},function(date){											})				})								$('#allup').click(function(){					var status = 1;					var village_id = [];					$('.single_select').each(function(){						if($(this).is(':checked')){							village_id.push($(this).val())						}					})					$.post('{pigcms{:U("ajax_change_adver_village_all")}',{status:status,village_id:village_id,adver_id:'{pigcms{$_GET.adver_id}'},function(date){						window.location.reload();					})				})								$('#alldown').click(function(){					var status = 0;					var village_id = [];					$('.single_select').each(function(){						if($(this).is(':checked')){							village_id.push($(this).val())						}					})					$.post('{pigcms{:U("ajax_change_adver_village_all")}',{status:status,village_id:village_id,adver_id:'{pigcms{$_GET.adver_id}'},function(date){						window.location.reload();					})				})				var flag= true;				$('#select_all').click(function(){					if(flag){						$('.single_select').attr('checked',flag);						flag = false;					}else{						$('.single_select').attr('checked',flag);						flag = true;											}				})			})		</script><include file="Public:footer"/>