<include file="Public:header"/><style>	img{		width:100px;		height:50px;	}</style>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('House/open_door_adver')}" class="on">开门广告列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('House/open_door_adver_add')}','添加广告',600,400,true,false,false,addbtn,'add',true);">添加广告</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup><col> <col> <col><col><col><col><col><col width="180" align="center"> </colgroup>						<thead>							<tr>								<th>编号</th>								<th>社区id</th>								<th>广告名称</th>								<th>覆盖小区</th>								<th>广告图片</th>								<th>链接</th>								<th>点击数</th>                                <th>广告状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($adver_list)">								<volist name="adver_list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.village_id}</td>										<td>{pigcms{$vo.name}</td>										<td><a href="{pigcms{:U('House/open_door_adv_village',array('adver_id'=>$vo['id']))}"  style="color:blue">指定小区</a></td>										<!--td>{pigcms{$vo.begin_time|date='Y-m-d',###}——{pigcms{$vo.end_time|date='Y-m-d',###}</td-->										<td><img src="{pigcms{$vo.android_pic}" class="view_msg"  ></td>										<td><a target="_blank" href="{pigcms{$vo.url}">链接</a></td>										<td>{pigcms{$vo.click_num}</td>                                        <if condition="$vo.status eq 1">                                            <td>启用</td>                                            <else/>                                            <td>关闭</td>                                        </if>																				<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('House/open_door_adver_edit',array('id'=>$vo['id'],'frame_show'=>true))}','查看广告详情',600,400,true,false,false,false,'add',true);">查看</a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('House/open_door_adver_edit',array('id'=>$vo['id']))}','编辑广告详情',600,400,true,false,false,editbtn,'add',true);">编辑</a> | <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('House/open_door_adver_del')}">删除</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="10">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="10">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>