<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Index/account')}" class="on">账号列表</a>|					<a href="javascript:void(0);"  onclick="window.top.artiframe('{pigcms{:U('Index/admin',array('area_id'=>$_GET['area_id']))}','添加管理账号',500,450,true,false,false,addbtn,'add',true);">添加管理员</a>|					<a href="{pigcms{:U('Index/authority_group')}" >权限套餐</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td style="width:50%;">						<form action="{pigcms{:U('Index/account')}" method="get">							<input type="hidden" name="c" value="Index"/>							<input type="hidden" name="a" value="account"/>							筛选: 							<div id="choose_pca" province_idss="{pigcms{$_GET.province_idss}" city_idss="{pigcms{$_GET.city_idss}" area_id="{pigcms{$_GET.area_id}" style="display:inline"></div>							<input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="realname" <if condition="$_GET['searchtype'] eq 'realname'">selected="selected"</if>>管理员姓名</option>																<option value="account" <if condition="$_GET['searchtype'] eq 'account'">selected="selected"</if>>管理员帐号</option>								<option value="phone" <if condition="$_GET['searchtype'] eq 'phone'">selected="selected"</if>>联系电话</option>								<option value="admin_id" <if condition="$_GET['searchtype'] eq 'admin_id'">selected="selected"</if>>管理员编号</option>							</select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;																					<input type="submit" value="查询" class="button"/>						</form>					</td>								</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup><col> <col> <col> <col><col><col><col><col><col><col width="240" align="center"> </colgroup>						<thead>							<tr>								<th>编号</th>								<th>帐号</th>								<th>级别</th>								<th>区域</th>								<th>微信昵称</th>								<th>姓名</th>								<th>电话</th>								<th>Email</th>								<th>QQ</th>								<th>最后登录时间</th>								<th class="textcenter">登录次数</th>								<th class="textcenter">状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($admins)">								<volist name="admins" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.account}</td>										<td>{pigcms{$vo.level_name}</td>										<td><php>if($vo['level']==1){</php><if condition="$vo.area_id gt 0 AND $vo.area_ip_desc neq ''">{pigcms{$vo.area_ip_desc}<elseif condition="in_array($vo['area_id'],$direct_city)" />{pigcms{$vo.area_name}市<elseif condition="in_array($vo['area_id'],$special_city)" />{pigcms{$vo.area_name}<else />{pigcms{$vo.area_name}省</if><php>}</php></td>										<td class="nickname">											<if condition="empty($vo['openid'])">                                            <a href="{pigcms{$config.site_url}/index.php?g=Index&c=Recognition&a=see_tmp_qrcode&qrcode_id={pigcms{$vo['id'] + 3890000000}&img=1" data-id="{pigcms{$vo['id']}" class="see_qrcode" id="qr_{pigcms{$vo['id']}" style="color:green">绑定微信号</a>                                            <else/>                                            {pigcms{$vo.nickname}                                            </if>                                         </td>										<td>{pigcms{$vo.realname}</td>										<td>{pigcms{$vo.phone}</td>										<td>{pigcms{$vo.email}</td>										<td>{pigcms{$vo.qq}</td>										<td><if condition="$vo['last_time']">{pigcms{$vo.last_time|date='Y-m-d H:i:s',###}<else/>无</if></td>										<td class="textcenter">{pigcms{$vo.login_count}</td>										<td class="textcenter"><if condition="$vo['status'] eq 1"><span style="color:green">正常</span><else /><span style="color:red">关闭</span></if></td>										<td class="textcenter">										<if condition="$vo['level'] neq 2">										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Index/admin',array('id'=>$vo['id']))}','编辑管理账号信息',500,320,true,false,false,editbtn,'edit',true);">编辑</a> |										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Index/menu',array('admin_id'=>$vo['id']))}','分配权限',800,500,true,false,false,editbtn,'edit',true);">分配权限</a>										<if condition="$_SESSION['system']['level'] eq 2">| <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('account_del')}">删除</a></if>										</if>										<if condition="$_SESSION['system']['level'] eq 2">										| <a class="label label-warning cancel" title="取消微信绑定" data-id="{pigcms{$vo['id']}" style="<if condition="empty($vo['openid'])">display:none;</if>color:red" >取消绑定</a>										</if>										</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="13">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="13">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>	<if condition="$system_session['area_type'] lt 3 "><script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script></if><script type="text/javascript" src="./static/js/artdialog/jquery.artDialog.js"></script><script type="text/javascript" src="./static/js/artdialog/iframeTools.js"></script><script type="text/javascript">var test;$(document).ready(function(){	$('.see_qrcode').live('click', function(){		test = art.dialog.open($(this).attr('href'),{			init: function(){				var iframe = this.iframe.contentWindow;				window.top.art.dialog.data('iframe_handle',iframe);			},			id: 'handle',			title:'扫描二维码绑定微信号',			padding: 0,			width: 430,			height: 433,			lock: true,			resize: false,			background:'black',			button: null,			fixed: false,			close: function(){clearInterval(t);},			left: '50%',			top: '38.2%',			opacity:'0.4'		});		var id = $(this).attr('data-id'), obj = $(this);	 	var t = window.setInterval(function(){			$.get("{pigcms{:U('Index/check_account')}", {id:id},  function(result){				if (result.error_code == 0) {					test.close();					clearInterval(t);					obj.parent('td').html(result.nickname).siblings('td').children('.cancel').show();				}			}, 'json');		},3000);		return false;	});	$('.cancel').click(function(){		var id = $(this).attr('data-id'), obj = $(this);		obj.attr('disabled', true);		$.get("{pigcms{:U('Index/cancel_account')}", {id:id}, function(result){			obj.attr('disabled', false);			if (result.error_code == 1) {				alert(result.msg);			} else {				var qrcode_id = 3890000000 + id;				obj.hide().parent('td').siblings('.nickname').html('<a href="{pigcms{$config.site_url}/index.php?g=Index&c=Recognition&a=see_tmp_qrcode&qrcode_id=' + qrcode_id + '&img=1" data-id="' + id + '" class="see_qrcode" style="color:green">绑定微信号</a>');			}		}, 'json');	});});</script><include file="Public:footer"/>