<include file="Public:header"/><script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Market/index')}" class="on">批发市场</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Market/index')}" method="get">							<input type="hidden" name="c" value="Market"/>							<input type="hidden" name="a" value="index"/>筛选:                             <if condition="$admin_area neq 3">                            <div id="choose_pca" province_idss="{pigcms{$_GET.province_idss}" city_idss="{pigcms{$_GET.city_idss}" area_id="{pigcms{$_GET.area_id}" style="display:inline"></div>                            </if>商户状态: <select name="status">                                <option value="-1" <if condition="$_GET['status'] eq -1">selected="selected"</if>>全部</option>                                <option value="0" <if condition="$_GET['status'] eq 0">selected="selected"</if>>待审核</option>                                <option value="1" <if condition="$_GET['status'] eq 1">selected="selected"</if>>审核通过</option>                                <option value="2" <if condition="$_GET['status'] eq 2">selected="selected"</if>>审核未通过</option>                                <option value="3" <if condition="$_GET['status'] eq 3">selected="selected"</if>>商家自行下架</option>                            </select>                            <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}" placeholder="请输入商品名称"/>							<input type="submit" value="查询" class="button"/>						</form>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>商品条形码</th>								<th>商品名称</th>								<th>商品图片</th>								<th>批发单价（元）</th>								<th>库存</th>								<th>最低批发数</th>								<th>已售</th>								<th>优惠明细</th>								<th>卖家信息</th>								<th>状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($orders)">								<volist name="orders" id="vo">									<tr>										<td>{pigcms{$vo.number}</td>										<td>{pigcms{$vo.name}</td>										<td><img src="{pigcms{$vo.pic}" width="50" height="50" class="view_msg"></td>										<td>{pigcms{$vo.price|floatval}</td>										<td>{pigcms{$vo.stock_num}/{pigcms{$vo.unit}</td>										<td>{pigcms{$vo.min_num}/{pigcms{$vo.unit}</td>										<td>{pigcms{$vo.sell_count}/{pigcms{$vo.unit}</td>										<td>{pigcms{$vo.discount_info_txt}</td>										<td>										                 商家名:{pigcms{$vo.merchant_name}<br/>										                 商家电话:<a>{pigcms{$vo.merchant_phone}</a><br/>										                 店铺名:{pigcms{$vo.store_name}<br/>										                 店铺电话:<a>{pigcms{$vo.merchant_phone}</a><br/>										</td>                                        <td><php>if ($vo['status'] == 0) {</php><span style="color: red">审核中</span><php>} elseif ($vo['status'] == 1) {</php><span style="color: green">正常</span><php> } elseif ($vo['status'] == 2) {</php><span style="color: gray">不通过</span><php>} elseif ($vo['status'] == 3) {</php><span style="color: gray">商家下架</span><php>}</php></td>										<td class="textcenter">										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Market/change',array('goods_id' => $vo['goods_id']))}','更改状态',400,180,true,false,false,editbtn,'add',true);">审核状态</a>										| <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Market/goods_detail',array('goods_id' => $vo['goods_id'],'frame_show'=>true))}','查看商品详情',720,520,true,false,false,false,'detail',true);">查看</a>										| <a href="{pigcms{:U('Market/order',array('goods_id' => $vo['goods_id']))}">查看售出详情</a>                                        | <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo['goods_id']}" url="{pigcms{:U('goods_del')}">删除</a>										</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="13">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="13">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>