<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Meal/label')}" class="on">店铺标签列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Meal/label_add')}','添加标签',480,160,true,false,false,addbtn,'add',true);">添加标签</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>名称</th>								<!--th>图标</th-->								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($labels)">								<volist name="labels" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.name}</td>										<!--td><img src="{pigcms{$vo.icon}" style="width:50px;height:50px;"></td-->										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Meal/label_edit',array('id'=>$vo['id']))}','编辑标签',480,160,true,false,false,editbtn,'add',true);">编辑</a> | <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('Meal/label_del')}">删除</a></td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="4">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>